import React, { useState } from 'react';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' },
  ];

  const handleNavClick = (item) => {
    if (item.page === 'products') {
      // Si estamos en contacto, primero vamos a home y luego hacemos scroll
      if (currentPage !== 'home') {
        setCurrentPage('home');
        setTimeout(() => {
          scrollToProducts();
        }, 100); // espera a que Home se renderice
      } else {
        scrollToProducts();
      }
    } else {
      setCurrentPage(item.page);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }

    setIsMenuOpen(false);
  };

  return (
    <header className="fixed w-full top-0 z-50 bg-white/90 backdrop-blur-md shadow-sm py-3">
      <nav className="container mx-auto px-4 flex items-center justify-between">
        <button onClick={() => handleNavClick({ page: 'home' })} className="font-bold text-purple-700 text-2xl">
          Mi Tienda!
        </button>

        {/* Desktop */}
        <div className="hidden md:flex gap-6">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => handleNavClick(item)}
              className="text-purple-700 hover:text-purple-500 font-medium"
            >
              {item.name}
            </button>
          ))}
        </div>

        {/* Mobile */}
        <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-purple-700">
          {isMenuOpen ? 'Cerrar' : 'Menu'}
        </button>

        {isMenuOpen && (
          <div className="md:hidden bg-white shadow-md mt-2 p-4 flex flex-col gap-3">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item)}
                className="text-purple-700 font-medium text-left"
              >
                {item.name}
              </button>
            ))}
          </div>
        )}
      </nav>
    </header>
  );
};

export default Header;
