import React, { useState, useEffect } from 'react';
import { Menu, X, Sun, Moon, Globe } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';

const Header = ({
  setCurrentPage,
  currentPage,
  scrollToProducts,
  theme,
  setTheme,
  language,
  setLanguage,
}) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  // Multi idioma
  const t = {
    es: {
      home: 'Inicio',
      products: 'Productos',
      contact: 'Contacto',
      store: 'Mi Tienda',
    },
    en: {
      home: 'Home',
      products: 'Products',
      contact: 'Contact',
      store: 'My Store',
    },
  };

  const navItems = [
    { name: t[language].home, page: 'home' },
    { name: t[language].products, page: 'products' },
    { name: t[language].contact, page: 'contact' },
  ];

  const handleNavClick = (page) => {
    if (page === 'products') {
      if (currentPage !== 'home') {
        setCurrentPage('home');
        setTimeout(() => scrollToProducts(), 100);
      } else {
        scrollToProducts();
      }
    } else {
      setCurrentPage(page);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled
          ? 'bg-white/80 dark:bg-gray-900/90 backdrop-blur-md shadow-md py-3'
          : 'bg-transparent dark:bg-gray-900/80 py-4'
      }`}
    >
      <nav className="container mx-auto px-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <button
            onClick={() => handleNavClick('home')}
            className="flex items-center space-x-2 font-bold text-xl md:text-2xl tracking-tight text-gray-900 dark:text-white"
          >
            <span className="text-amber-500">{t[language].store.split(' ')[0]}</span>
            <span>{t[language].store.split(' ')[1]}</span>
          </button>

          {/* Desktop Nav */}
          <div className="hidden md:flex items-center space-x-10">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="relative font-medium text-gray-700 dark:text-gray-200 hover:text-amber-500 transition-all group"
              >
                {item.name}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-amber-500 group-hover:w-full transition-all duration-300"></span>
              </button>
            ))}

            {/* Theme toggle */}
            <button
              onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
              className="text-gray-700 dark:text-gray-200 hover:text-amber-500 transition-colors"
            >
              {theme === 'dark' ? <Sun size={22} /> : <Moon size={22} />}
            </button>

            {/* Language toggle */}
            <button
              onClick={() => setLanguage(language === 'es' ? 'en' : 'es')}
              className="text-gray-700 dark:text-gray-200 hover:text-amber-500 transition-colors"
            >
              <Globe size={22} />
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden text-gray-700 dark:text-gray-200 hover:text-amber-500 transition-colors"
          >
            {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>
      </nav>

      {/* Mobile Sidebar */}
      <div
        className={`fixed top-0 left-0 h-full w-64 bg-white dark:bg-gray-900 shadow-2xl transform transition-transform duration-500 ease-in-out ${
          isMenuOpen ? 'translate-x-0' : '-translate-x-full'
        }`}
      >
        <div className="flex items-center justify-between px-4 py-4 border-b border-gray-200 dark:border-gray-700">
          <h2 className="font-semibold text-gray-900 dark:text-white">
            {t[language].store}
          </h2>
          <button
            onClick={() => setIsMenuOpen(false)}
            className="text-gray-700 dark:text-gray-200"
          >
            <X size={24} />
          </button>
        </div>

        <div className="flex flex-col px-4 mt-6 space-y-4">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => handleNavClick(item.page)}
              className="text-left text-gray-700 dark:text-gray-200 text-lg hover:text-amber-500 transition-all"
            >
              {item.name}
            </button>
          ))}

          <div className="flex items-center justify-between mt-6 border-t pt-4 border-gray-200 dark:border-gray-700">
            <button
              onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
              className="text-gray-700 dark:text-gray-200 hover:text-amber-500"
            >
              {theme === 'dark' ? <Sun size={24} /> : <Moon size={24} />}
            </button>
            <button
              onClick={() => setLanguage(language === 'es' ? 'en' : 'es')}
              className="text-gray-700 dark:text-gray-200 hover:text-amber-500"
            >
              <Globe size={24} />
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
