import React, { useState, useEffect } from 'react';
import { ShoppingBag, Menu, X } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';

const Header = ({ setCurrentPage, currentPage }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' }
  ];

  const scrollToProducts = () => {
    const element = document.getElementById('productos');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleNavClick = (page) => {
    if (page === 'products' && currentPage === 'home') {
      scrollToProducts();
    } else {
      setCurrentPage(page);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <header className={`fixed w-full top-0 z-50 transition-all duration-500 ${
      isScrolled 
        ? 'bg-white/90 backdrop-blur-md shadow-sm py-3' 
        : 'bg-white/80 backdrop-blur-sm py-4'
    }`}>
      <nav className="container mx-auto px-4">
        <div className="flex items-center justify-between">
          
          {/* Logo */}
          <button 
            onClick={() => handleNavClick('home')}
            className="flex items-center space-x-2 group"
          >
            <div className={`bg-gradient-to-br from-purple-200 via-pink-200 to-orange-200 p-2 rounded-xl transform group-hover:scale-105 transition-all duration-300 ${isScrolled ? 'shadow' : ''}`}>
              <ShoppingBag className="text-purple-700" size={isScrolled ? 22 : 24} />
            </div>
            <span className={`font-bold text-purple-700 transition-all duration-300 ${isScrolled ? 'text-xl' : 'text-2xl'}`}>
              Mi Tienda!
            </span>
          </button>

          {/* Desktop Nav */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="text-purple-700 hover:text-purple-500 font-medium transition-all duration-300 relative group"
              >
                {item.name}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-purple-300 group-hover:w-full transition-all duration-300"></span>
              </button>
            ))}
            <button className="bg-gradient-to-r from-purple-200 via-pink-200 to-orange-200 text-purple-700 px-6 py-2 rounded-full hover:shadow-md transform hover:scale-105 transition-all duration-300 font-medium">
              Carrito (0)
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden text-purple-700 hover:text-purple-500 transition-colors"
          >
            {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden mt-4 pb-4 animate-slide-down">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="block w-full text-left py-3 text-purple-700 hover:text-purple-500 hover:bg-purple-50 px-4 rounded-lg transition-all duration-300"
              >
                {item.name}
              </button>
            ))}
            <button className="w-full mt-4 bg-gradient-to-r from-purple-200 via-pink-200 to-orange-200 text-purple-700 px-6 py-3 rounded-full hover:shadow-md transform hover:scale-105 transition-all duration-300">
              Carrito (0)
            </button>
          </div>
        )}
      </nav>
    </header>
  );
};

export default Header;
