import React, { useState } from 'react';

const Header = ({ setCurrentPage, currentPage }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'home' }, // Productos está en Home
    { name: 'Contacto', page: 'contact' }
  ];

  const scrollToProducts = () => {
    const element = document.getElementById('productos');
    if (element) element.scrollIntoView({ behavior: 'smooth' });
  };

  const handleNavClick = (page) => {
    if (page === 'home') {
      setCurrentPage('home');
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    if (page === 'contact') {
      setCurrentPage('contact');
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    if (page === 'home' && page === 'home') scrollToProducts(); // Para Productos
    setIsMenuOpen(false);
  };

  return (
    <header className="fixed w-full top-0 z-50 bg-white/90 backdrop-blur-md shadow-sm py-3">
      <nav className="container mx-auto px-4 flex items-center justify-between">
        <button onClick={() => handleNavClick('home')} className="font-bold text-purple-700 text-2xl">
          Mi Tienda!
        </button>

        {/* Desktop */}
        <div className="hidden md:flex gap-6">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => {
                if (item.name === 'Productos') scrollToProducts();
                else handleNavClick(item.page);
              }}
              className="text-purple-700 hover:text-purple-500 font-medium"
            >
              {item.name}
            </button>
          ))}
        </div>

        {/* Mobile */}
        <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden text-purple-700">
          {isMenuOpen ? 'Cerrar' : 'Menu'}
        </button>
      </nav>

      {isMenuOpen && (
        <div className="md:hidden bg-white shadow-md mt-2 p-4 flex flex-col gap-3">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => {
                if (item.name === 'Productos') scrollToProducts();
                else handleNavClick(item.page);
              }}
              className="text-purple-700 font-medium text-left"
            >
              {item.name}
            </button>
          ))}
        </div>
      )}
    </header>
  );
};

export default Header;
