import React, { useState, useEffect, useCallback } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') scrollToProducts();
        else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
      } else {
        setCurrentPage(item.page);
        if (item.page === 'home') window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  return (
    <header className="fixed w-full z-50 backdrop-blur-md bg-white/90 dark:bg-gray-900/90 shadow-md transition-colors duration-500">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
        {/* Logo */}
        <motion.div
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="text-2xl font-extrabold text-gray-900 dark:text-gray-100 cursor-pointer hover:text-amber-500 transition-colors duration-500"
          onClick={() => {
            setCurrentPage('home');
            window.scrollTo({ top: 0, behavior: 'smooth' });
          }}
        >
          Tienda
        </motion.div>

        {/* Navegación desktop */}
        <nav className="hidden md:flex items-center gap-8">
          {menuItems.map((item) => (
            <motion.button
              key={item.name}
              onClick={() => handleMenuClick(item)}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className={`font-semibold text-gray-900 dark:text-gray-200 transition-colors duration-500 focus:outline-none ${
                currentPage === item.page ? 'text-amber-400' : 'hover:text-amber-400'
              }`}
            >
              {item.name}
            </motion.button>
          ))}
        </nav>

        {/* Botones: modo oscuro y menú móvil */}
        <div className="flex items-center gap-4 relative z-50">
          {/* Toggle tema */}
          <motion.div
            whileHover={{ scale: 1.1, boxShadow: '0 4px 12px rgba(251,191,36,0.4)' }}
            whileTap={{ scale: 0.9 }}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
            hover:bg-amber-400 dark:hover:bg-amber-500 hover:text-gray-900 dark:hover:text-gray-900 
            transition-all duration-500 ease-in-out shadow-sm cursor-pointer select-none"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              toggleTheme();
            }}
          >
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
          </motion.div>

          {/* Botón menú móvil con animación de línea */}
          <motion.button
            className="md:hidden relative w-10 h-10 flex flex-col justify-center items-center gap-1.5 p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 shadow-sm cursor-pointer transition-all duration-500 hover:bg-amber-400 dark:hover:bg-amber-500"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <motion.span
              animate={{ rotate: isMenuOpen ? 45 : 0, y: isMenuOpen ? 7 : 0 }}
              className="block w-6 h-0.5 bg-current rounded"
            />
            <motion.span
              animate={{ opacity: isMenuOpen ? 0 : 1 }}
              className="block w-6 h-0.5 bg-current rounded"
            />
            <motion.span
              animate={{ rotate: isMenuOpen ? -45 : 0, y: isMenuOpen ? -7 : 0 }}
              className="block w-6 h-0.5 bg-current rounded"
            />
          </motion.button>
        </div>
      </div>

      {/* Overlay y menú móvil */}
      <AnimatePresence>
        {isMenuOpen && (
          <>
            {/* Overlay */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 0.5 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              className="fixed inset-0 bg-black z-40"
              onClick={() => setIsMenuOpen(false)}
            />

            {/* Menú lateral */}
            <motion.nav
              initial={{ x: '100%' }}
              animate={{ x: 0 }}
              exit={{ x: '100%' }}
              transition={{ type: 'spring', stiffness: 300, damping: 30 }}
              className="fixed top-0 right-0 h-full w-72 bg-white dark:bg-gray-900 z-50 shadow-2xl backdrop-blur-lg p-6 flex flex-col"
            >
              <div className="flex justify-between items-center mb-8">
                <div className="text-xl font-bold text-gray-900 dark:text-gray-100">Menú</div>
                <button
                  onClick={() => setIsMenuOpen(false)}
                  className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 hover:bg-amber-400 dark:hover:bg-amber-500 transition-all duration-300"
                >
                  <X size={20} />
                </button>
              </div>

              <div className="flex flex-col gap-4 mt-4">
                {menuItems.map((item) => (
                  <motion.button
                    key={item.name}
                    onClick={() => {
                      handleMenuClick(item);
                      setIsMenuOpen(false);
                    }}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="text-gray-900 dark:text-gray-200 font-semibold py-3 px-4 rounded-lg 
                      hover:bg-amber-100 dark:hover:bg-amber-500 transition-colors duration-300 text-left"
                  >
                    {item.name}
                  </motion.button>
                ))}
              </div>
            </motion.nav>
          </>
        )}
      </AnimatePresence>
    </header>
  );
};

export default Header;
