import React, { useState } from 'react';
import { Heart, Star, Sparkles, ShoppingBag } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';

const ProductCard = ({ product, index }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [ref, isVisible] = useIntersectionObserver();

  return (
    <div 
      ref={ref}
      className={`bg-white rounded-3xl overflow-hidden shadow-xl hover:shadow-2xl transform hover:-translate-y-4 transition-all duration-500 group ${
        isVisible ? 'animate-scale-in' : 'opacity-0'
      }`}
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div className="relative overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-80 object-cover group-hover:scale-125 group-hover:rotate-2 transition-all duration-700"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
        
        <button
          onClick={() => setIsFavorite(!isFavorite)}
          className="absolute top-4 right-4 bg-white/90 backdrop-blur-sm p-3 rounded-full hover:bg-white transition-all shadow-lg hover:scale-125 duration-300"
        >
          <Heart
            size={20}
            className={`${isFavorite ? 'fill-red-500 text-red-500 animate-heart-beat' : 'text-gray-600'} transition-colors`}
          />
        </button>
        
        {product.discount && (
          <div className="absolute top-4 left-4 bg-gradient-to-r from-red-500 to-pink-500 text-white px-4 py-2 rounded-full text-sm font-bold shadow-lg animate-pulse-slow">
            -{product.discount}%
          </div>
        )}
        
        <div className="absolute bottom-4 left-4 right-4 opacity-0 group-hover:opacity-100 transform translate-y-4 group-hover:translate-y-0 transition-all duration-500">
          <button className="w-full bg-white text-purple-600 py-3 rounded-full font-bold hover:bg-gradient-to-r hover:from-purple-600 hover:to-pink-500 hover:text-white transition-all shadow-xl flex items-center justify-center gap-2">
            <Sparkles size={18} />
            Vista Rápida
          </button>
        </div>
      </div>
      
      <div className="p-6">
        <div className="flex items-center gap-1 mb-3">
          {[...Array(5)].map((_, i) => (
            <Star key={i} size={16} className="fill-yellow-400 text-yellow-400" />
          ))}
          <span className="text-sm text-gray-600 ml-2 font-semibold">(128)</span>
        </div>
        
        <h3 className="text-xl font-bold text-gray-800 mb-2 group-hover:text-purple-600 transition-all duration-300">
          {product.name}
        </h3>
        <p className="text-sm text-gray-600 mb-4 font-medium">{product.category}</p>
        
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            {product.originalPrice && (
              <span className="text-gray-400 line-through text-sm">${product.originalPrice}</span>
            )}
            <span className="text-3xl font-bold bg-gradient-to-r from-purple-600 to-pink-500 bg-clip-text text-transparent">
              ${product.price}
            </span>
          </div>
          <button className="bg-gradient-to-r from-purple-600 via-pink-500 to-orange-400 text-white p-3 rounded-full hover:shadow-2xl hover:shadow-purple-500/50 transform hover:scale-125 hover:rotate-12 transition-all duration-300">
            <ShoppingBag size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
