import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' },
  ];

  const handleNavClick = (page) => {
    if (page === 'products') {
      scrollToProducts();
    } else {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled ? 'bg-white/90 backdrop-blur-md shadow-md py-2' : 'bg-white/60 backdrop-blur-sm py-4'
      }`}
    >
      <nav className="container mx-auto px-6">
        <div className="flex items-center justify-between md:justify-between relative">
          {/* Mobile: hamburguesa a la izquierda */}
          <div className="flex items-center md:hidden">
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="transition-transform duration-300 hover:scale-110 text-gray-800 hover:text-amber-500"
            >
              {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
            </button>
          </div>

          {/* Logo centrado en m√≥viles, normal en desktop */}
          <button
            onClick={() => handleNavClick('home')}
            className="absolute left-1/2 transform -translate-x-1/2 md:static md:transform-none flex items-center space-x-2 font-extrabold text-2xl tracking-tight hover:scale-105 transition-transform duration-300 text-gray-900"
          >
            <span className="text-amber-500">Mi</span>
            <span>Tienda</span>
          </button>

          {/* Desktop Nav */}
          <div className="hidden md:flex items-center space-x-10">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="relative font-medium text-lg transition-all duration-300 group text-gray-800 hover:text-amber-500"
              >
                {item.name}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-amber-400 group-hover:w-full transition-all duration-300 rounded"></span>
              </button>
            ))}
          </div>
        </div>

        {/* Mobile Menu Overlay */}
        <div
          className={`md:hidden fixed inset-0 z-50 bg-black/20 backdrop-blur-sm transition-opacity duration-300 ease-in-out ${
            isMenuOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
        >
          <div
            className={`bg-gradient-to-br from-blue-200 via-blue-100 to-amber-100 w-full h-screen p-8 flex flex-col justify-center items-center space-y-8 transform transition-transform duration-500 ease-in-out ${
              isMenuOpen ? 'translate-y-0' : '-translate-y-full'
            } overflow-y-auto relative`}
          >
            {/* Bot√≥n cerrar X fijo en top-right */}
            <button
              onClick={() => setIsMenuOpen(false)}
              className="absolute top-6 right-6 text-gray-900 hover:text-amber-500 transition-colors"
            >
              <X size={32} />
            </button>

            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="text-3xl font-semibold transition-all duration-300 hover:scale-105 text-gray-900 hover:text-amber-500"
              >
                {item.name}
              </button>
            ))}
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
