import React, { useState, useEffect } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import { useTheme } from '../context/ThemeContext';
import { useLanguage } from '../context/LanguageContext';
import translations from '../i18n/translations';

const ICON_TRANSITION = { duration: 0.4, ease: 'easeInOut' };

const Header = ({ setCurrentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();
  const { theme, toggleTheme } = useTheme();
  const { language, toggleLanguage } = useLanguage();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const handleNavClick = (page) => {
    if (page === 'products') scrollToProducts();
    else window.scrollTo({ top: 0, behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  const ThemeToggleButton = ({ size = 20 }) => (
    <button
      onClick={toggleTheme}
      className="p-2 rounded-full bg-neutral-200 dark:bg-neutral-700 hover:scale-110 transition-transform duration-300"
    >
      <AnimatePresence mode="wait" initial={false}>
        <motion.span
          key={theme}
          initial={{ rotate: -90, opacity: 0, scale: 0.5 }}
          animate={{ rotate: 0, opacity: 1, scale: 1 }}
          exit={{ rotate: 90, opacity: 0, scale: 0.5 }}
          transition={ICON_TRANSITION}
        >
          {theme === 'dark' ? <Sun size={size} /> : <Moon size={size} />}
        </motion.span>
      </AnimatePresence>
    </button>
  );

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled
          ? 'bg-white/90 dark:bg-neutral-900/90 backdrop-blur-md shadow-md py-2'
          : 'bg-white/60 dark:bg-neutral-900/60 backdrop-blur-sm py-4'
      }`}
    >
      <nav className="container mx-auto px-6">
        <div className="flex items-center justify-between relative">
          {/* Botón menú móvil */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden transition-transform duration-300 hover:scale-110 text-gray-800 dark:text-gray-200 hover:text-amber-500"
            aria-label="Toggle menu"
          >
            {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>

          {/* Logo */}
          <button
            onClick={() => handleNavClick('home')}
            className="absolute left-1/2 transform -translate-x-1/2 md:static md:transform-none flex items-center space-x-2 font-extrabold text-2xl tracking-tight hover:scale-105 transition-transform duration-300 text-gray-900 dark:text-gray-100"
          >
            <span className="text-amber-500">Mi</span>
            <span>Tienda</span>
          </button>

          {/* Navegación Desktop */}
          <div className="hidden md:flex items-center space-x-4">
            {['home', 'products', 'contact'].map((page) => (
              <button
                key={page}
                onClick={() => handleNavClick(page)}
                className="relative font-medium text-lg transition-all duration-300 group text-gray-800 dark:text-gray-200 hover:text-amber-500"
              >
                {translations[language].header[page]}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-amber-400 group-hover:w-full transition-all duration-300 rounded"></span>
              </button>
            ))}

            <ThemeToggleButton />

            {/* Botón de idioma Desktop */}
            <button
              onClick={toggleLanguage}
              className="ml-4 px-3 py-1 border rounded-full text-sm font-medium text-gray-800 dark:text-gray-200 hover:bg-amber-500 hover:text-white transition-colors"
            >
              {translations[language].header.toggleLanguage}
            </button>
          </div>
        </div>

        {/* Menú móvil desplegable */}
        <AnimatePresence>
          {isMenuOpen && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="md:hidden fixed inset-0 z-40 bg-black/20 backdrop-blur-sm"
            >
              <motion.div
                initial={{ y: '-100%' }}
                animate={{ y: 0 }}
                exit={{ y: '-100%' }}
                transition={{ duration: 0.5, ease: 'easeInOut' }}
                className="bg-gradient-to-br from-blue-100 via-amber-50 to-white dark:from-neutral-900 dark:via-neutral-800 dark:to-neutral-900 w-full h-screen p-8 flex flex-col justify-center items-center space-y-8 relative overflow-y-auto"
              >
                <button
                  onClick={() => setIsMenuOpen(false)}
                  className="absolute top-6 left-6 text-gray-900 dark:text-gray-100 hover:text-amber-500 transition-colors"
                  aria-label="Close menu"
                >
                  <X size={32} />
                </button>

                {['home', 'products', 'contact'].map((page) => (
                  <button
                    key={page}
                    onClick={() => handleNavClick(page)}
                    className="text-3xl font-semibold transition-all duration-300 hover:scale-105 text-gray-900 dark:text-gray-100 hover:text-amber-500"
                  >
                    {translations[language].header[page]}
                  </button>
                ))}

                <ThemeToggleButton size={26} />

                {/* Botón de idioma Móvil */}
                <button
                  onClick={toggleLanguage}
                  className="mt-6 px-4 py-2 border rounded-full text-lg font-medium text-gray-800 dark:text-gray-200 hover:bg-amber-500 hover:text-white transition-colors"
                >
                  {translations[language].header.toggleLanguage}
                </button>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </nav>
    </header>
  );
};

export default Header;
