import React, { useState, useRef, useEffect } from 'react';
import { Sparkles, ArrowRight, ArrowLeft, Filter, X, ChevronLeft, ChevronRight } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';

const ProductList = () => {
  const { theme } = useTheme();
  const isDark = theme === 'dark';

  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 200]);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  const productsPerPage = 6;
  const categories = ['all', ...new Set(products.map(p => p.category))];
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];
  const containerRef = useRef(null);

  const filteredProducts = products
    .filter(p => (category === 'all' ? true : p.category === category))
    .filter(p => (gender === 'all' ? true : p.gender === gender))
    .filter(p => p.price >= priceRange[0] && p.price <= priceRange[1])
    .filter(p => {
      switch (collection) {
        case 'novedades':
        case 'nuevos':
          return p.isNew === true;
        case 'mas-vendidos':
          return p.isBestseller === true;
        case 'ofertas':
          return p.isOnSale === true;
        default:
          return true;
      }
    })
    .sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  return (
    <section
      className={`relative py-20 transition-colors duration-500 ${
        isDark
          ? 'bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900'
          : 'bg-gradient-to-b from-amber-100 via-white to-white'
      }`}
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Título */}
        <div className="text-center mb-12">
          <div
            className={`inline-flex items-center gap-2 px-6 py-2 rounded-full mb-4 shadow-sm transition-colors duration-500 ${
              isDark ? 'bg-gray-700' : 'bg-amber-200'
            }`}
          >
            <Sparkles
              className={`transition-colors duration-500 ${
                isDark ? 'text-amber-300' : 'text-amber-400'
              }`}
              size={20}
            />
            <span
              className={`text-sm font-bold transition-colors duration-500 ${
                isDark ? 'text-gray-200' : 'text-stone-800'
              }`}
            >
              Colección Exclusiva
            </span>
          </div>
          <h2
            className={`text-5xl md:text-6xl font-bold mb-4 transition-colors duration-500 ${
              isDark ? 'text-white' : 'text-stone-900'
            }`}
          >
            Nuestra Colección
          </h2>
          <p
            className={`text-xl md:text-2xl max-w-3xl mx-auto leading-relaxed transition-colors duration-500 ${
              isDark ? 'text-gray-300' : 'text-stone-600'
            }`}
          >
            Moda masculina y femenina con estilo. Encuentra tu look ideal entre nuestras selecciones exclusivas.
          </p>
        </div>

        {/* Botón filtros móviles */}
        <div className="flex justify-center mb-6 md:hidden">
          <button
            onClick={() => setIsFilterOpen(true)}
            className={`flex items-center gap-2 px-5 py-2.5 rounded-full font-semibold shadow transition-all duration-300 ${
              isDark
                ? 'bg-amber-600 text-gray-900 hover:bg-amber-700'
                : 'bg-amber-400 text-gray-900 hover:bg-amber-500'
            }`}
          >
            <Filter size={20} /> Filtros
          </button>
        </div>

        <div className="flex gap-8 relative">
          {/* Botón plegar/abrir sidebar desktop */}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className={`hidden md:flex absolute -left-5 top-2 z-10 items-center justify-center p-2 rounded-full shadow transition-all duration-300 ${
              isDark
                ? 'bg-amber-600 text-gray-900 hover:bg-amber-700'
                : 'bg-amber-400 text-gray-900 hover:bg-amber-500'
            }`}
          >
            {isSidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
          </button>

          {/* Sidebar filtros desktop */}
          <aside
            className={`hidden md:flex flex-col rounded-2xl p-6 shadow-md space-y-6 max-h-[calc(100vh-200px)] overflow-y-auto sticky top-24 transition-all duration-300 ${
              isDark ? 'bg-gray-800 text-gray-200' : 'bg-white text-stone-900'
            } ${isSidebarOpen ? 'w-72 opacity-100 mr-4' : 'w-0 opacity-0 p-0 mr-0 overflow-hidden'}`}
          >
            {isSidebarOpen && (
              <>
                <h3 className="text-lg font-bold mb-2">Filtros</h3>
                {/* ...aquí van los selects y sliders (mismos que tenías) */}
              </>
            )}
          </aside>

          {/* Productos */}
          <div className="flex-1">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.4 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product, index) => (
                    <ProductCard key={product.id} product={product} index={index} />
                  ))
                ) : (
                  <p className={`text-center text-lg col-span-full transition-colors duration-500 ${isDark ? 'text-gray-300' : 'text-stone-600'}`}>
                    No se encontraron productos que coincidan con los filtros seleccionados.
                  </p>
                )}
              </motion.div>
            </AnimatePresence>

            {/* Paginación */}
            {/* ...mantener igual pero con colores dark mejorados */}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
