import React from 'react';
import { ShoppingBag, Instagram, Facebook, Twitter } from 'lucide-react';

const Footer = () => {
  return (
    <footer className="bg-gradient-to-br from-gray-950 via-slate-900 to-gray-950 text-white pt-16 pb-8 relative overflow-hidden">
      {/* Fondo sutil con textura */}
      <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-10"></div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-12 mb-12">
          {/* Logo y redes */}
          <div className="animate-fade-in">
            <div className="flex items-center space-x-2 mb-4">
              <div className="bg-gradient-to-br from-indigo-600 via-purple-600 to-blue-500 p-2 rounded-xl transform hover:rotate-12 transition-transform duration-300">
                <ShoppingBag size={24} />
              </div>
              <span className="text-2xl font-bold tracking-tight">Mi Tienda</span>
            </div>
            <p className="text-gray-400 mb-6 leading-relaxed max-w-sm">
              Moda moderna para todos. Encuentra tu estilo con nuestras colecciones exclusivas.
            </p>
            <div className="flex gap-4">
              {[
                { icon: <Instagram size={20} />, label: "Instagram" },
                { icon: <Facebook size={20} />, label: "Facebook" },
                { icon: <Twitter size={20} />, label: "Twitter" },
              ].map((social, i) => (
                <button
                  key={i}
                  aria-label={social.label}
                  className="bg-gray-800 p-3 rounded-full hover:bg-gradient-to-r hover:from-indigo-600 hover:to-blue-500 transform hover:scale-110 transition-all duration-300"
                >
                  {social.icon}
                </button>
              ))}
            </div>
          </div>

          {/* Categorías */}
          <div>
            <h3 className="text-xl font-semibold mb-4 bg-gradient-to-r from-indigo-400 to-blue-400 bg-clip-text text-transparent">
              Categorías
            </h3>
            <ul className="space-y-3 text-gray-400">
              {["Hombre", "Mujer", "Niños", "Accesorios"].map((item) => (
                <li key={item}>
                  <button className="hover:text-white hover:translate-x-2 transition-all duration-300 inline-block">
                    {item}
                  </button>
                </li>
              ))}
            </ul>
          </div>

          {/* Ayuda */}
          <div>
            <h3 className="text-xl font-semibold mb-4 bg-gradient-to-r from-indigo-400 to-blue-400 bg-clip-text text-transparent">
              Ayuda
            </h3>
            <ul className="space-y-3 text-gray-400">
              {["Envíos", "Devoluciones", "Preguntas Frecuentes", "Términos y Condiciones"].map(
                (item) => (
                  <li key={item}>
                    <button className="hover:text-white hover:translate-x-2 transition-all duration-300 inline-block">
                      {item}
                    </button>
                  </li>
                )
              )}
            </ul>
          </div>
        </div>

        {/* Derechos reservados */}
        <div className="border-t border-gray-800 pt-8 text-center">
          <p className="text-gray-500 text-sm tracking-wide">
            &copy; 2025 Mi Tienda. Todos los derechos reservados.
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
