import React, { useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';

const Novedades = () => {
  const swiperRef = useRef(null);

  const novedades = [
    {
      id: 1,
      title: 'Campera Urbana 2025',
      category: 'Hombre',
      price: '$89.900',
      description: 'Ligera, impermeable y con diseño moderno. Ideal para la ciudad.',
      image:
        'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-blue-100 text-blue-800',
    },
    {
      id: 2,
      title: 'Vestido Verano 2025',
      category: 'Mujer',
      price: '$62.500',
      description: 'Fresco, fluido y elegante. Perfecto para días soleados.',
      image:
        'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-pink-100 text-pink-800',
    },
    {
      id: 3,
      title: 'Remera Clásica Unisex',
      category: 'Unisex',
      price: '$24.900',
      description: 'Algodón premium, corte perfecto. Tu esencial diario.',
      image:
        'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-gray-100 text-gray-800',
    },
    {
      id: 4,
      title: 'Pantalón Cargo',
      category: 'Hombre',
      price: '$74.900',
      description: 'Resistente, funcional y con bolsillos amplios.',
      image:
        'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-green-100 text-green-800',
    },
  ];

  return (
    <section
      className="py-16 sm:py-20 transition-colors duration-500"
      style={{ backgroundColor: '#fef3c7' }}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Encabezado */}
        <div className="text-center mb-10 sm:mb-14">
          <h2 className="text-3xl sm:text-5xl font-extrabold text-gray-900 mb-3 sm:mb-4">
            Novedades
          </h2>
          <p className="text-base sm:text-lg text-gray-700 max-w-2xl mx-auto">
            Descubrí lo último en estilo, confort y tendencia para esta temporada.
          </p>
        </div>

        {/* Carrusel */}
        <div
          onMouseEnter={() => swiperRef.current?.autoplay?.stop()}
          onMouseLeave={() => swiperRef.current?.autoplay?.start()}
        >
          <Swiper
            modules={[Pagination, Autoplay]}
            spaceBetween={20}
            slidesPerView={1}
            pagination={{ clickable: true, el: '.novedades-pagination' }}
            autoplay={{
              delay: 4000,
              disableOnInteraction: false,
            }}
            loop={true}
            onSwiper={(swiper) => (swiperRef.current = swiper)}
            className="relative rounded-2xl sm:rounded-3xl shadow-xl overflow-hidden"
          >
            {novedades.map((item) => (
              <SwiperSlide key={item.id}>
                <div className="flex flex-col md:grid md:grid-cols-2 h-full">
                  {/* Imagen */}
                  <div className="relative group">
                    <img
                      src={item.image}
                      alt={item.title}
                      className="w-full h-64 sm:h-80 md:h-full object-cover transition-transform duration-700 group-hover:scale-105"
                      loading="lazy"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
                    <span
                      className={`absolute top-4 left-4 px-3 py-1 text-xs sm:text-sm font-semibold rounded-full ${item.badgeColor} backdrop-blur-sm shadow-sm`}
                    >
                      {item.category}
                    </span>
                  </div>

                  {/* Texto */}
                  <div className="flex flex-col justify-center px-5 sm:px-8 py-8 sm:py-10 bg-white/90 backdrop-blur-md">
                    <h3 className="text-2xl sm:text-3xl md:text-4xl font-bold text-gray-900 mb-3 sm:mb-4">
                      {item.title}
                    </h3>
                    <p className="text-base sm:text-lg text-gray-700 leading-relaxed mb-5 sm:mb-6">
                      {item.description}
                    </p>
                    <div className="flex items-center justify-between mb-6 sm:mb-8">
                      <span className="text-2xl sm:text-3xl font-bold text-yellow-600">
                        {item.price}
                      </span>
                    </div>
                    <button className="px-6 py-3 sm:px-8 bg-yellow-500 text-gray-900 font-bold rounded-full shadow-md hover:bg-yellow-600 hover:shadow-lg hover:scale-105 transition-transform duration-300 text-sm sm:text-base">
                      Ver producto
                    </button>
                  </div>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>

          {/* Paginación */}
          <div className="novedades-pagination flex justify-center mt-6 sm:mt-8 gap-3"></div>
        </div>
      </div>

      {/* Estilo bullets */}
      <style jsx>{`
        .novedades-pagination .swiper-pagination-bullet {
          width: 10px;
          height: 10px;
          background-color: #fbbf24;
          opacity: 0.6;
          transition: all 0.3s ease;
        }
        .novedades-pagination .swiper-pagination-bullet-active {
          transform: scale(1.4);
          opacity: 1;
          background-color: #facc15;
        }
      `}</style>
    </section>
  );
};

export default Novedades;
