import React, { useState, useRef, useEffect } from 'react';
import { Sparkles, ArrowRight, ArrowLeft, Filter, X, ChevronLeft, ChevronRight } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';
import { useLanguage } from '../context/LanguageContext';
import translations from '../i18n/translations';

const ProductList = () => {
  const { theme } = useTheme();
  const { language } = useLanguage();
  const t = translations[language]?.productList || translations['es'].productList;

  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 200]);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  const productsPerPage = 6;
  const categories = ['all', ...new Set(products.map(p => p.category))];
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];
  const containerRef = useRef(null);

  // Filtrado y ordenamiento
  const filteredProducts = products
    .filter(p => (category === 'all' ? true : p.category === category))
    .filter(p => (gender === 'all' ? true : p.gender === gender))
    .filter(p => p.price >= priceRange[0] && p.price <= priceRange[1])
    .filter(p => {
      switch (collection) {
        case 'novedades':
        case 'nuevos':
          return p.isNew === true;
        case 'mas-vendidos':
          return p.isBestseller === true;
        case 'ofertas':
          return p.isOnSale === true;
        default:
          return true;
      }
    })
    .sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  // Scroll suave cuando cambia la pÃ¡gina
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  return (
    <section
      id="productos"
      className={`relative py-20 transition-colors duration-500 ${
        theme === 'dark' ? 'bg-neutral-900 text-white' : 'bg-gradient-to-b from-amber-100 via-white to-white text-gray-900'
      }`}
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* TÃ­tulo */}
        <div className="text-center mb-12">
          <div className={`inline-flex items-center gap-2 px-6 py-2 rounded-full mb-4 shadow-sm ${theme === 'dark' ? 'bg-amber-700/40 text-white' : 'bg-amber-200'}`}>
            <Sparkles size={20} className={theme === 'dark' ? 'text-amber-400' : 'text-amber-400'} />
            <span className="text-sm font-bold">{t.collectionLabel}</span>
          </div>
          <h2 className="text-5xl md:text-6xl font-bold mb-4">{t.title}</h2>
          <p className="text-xl md:text-2xl max-w-3xl mx-auto leading-relaxed">{t.description}</p>
        </div>

        {/* BotÃ³n filtros mÃ³viles */}
        <div className="flex justify-center mb-6 md:hidden">
          <button
            onClick={() => setIsFilterOpen(true)}
            className="flex items-center gap-2 bg-amber-400 text-gray-900 px-5 py-2.5 rounded-full font-semibold shadow hover:bg-amber-500 transition-all duration-300"
          >
            <Filter size={20} /> {t.filtersButton}
          </button>
        </div>

        <div className="flex gap-8 relative">
          {/* BotÃ³n plegar/abrir sidebar desktop */}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="hidden md:flex absolute -left-5 top-2 z-10 items-center justify-center bg-amber-400 text-gray-900 p-2 rounded-full shadow hover:bg-amber-500 transition-all duration-300"
          >
            {isSidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
          </button>

          {/* Sidebar filtros desktop */}
          <aside
            className={`hidden md:flex flex-col rounded-2xl p-6 shadow-md space-y-6 
                        max-h-[calc(100vh-200px)] overflow-y-auto sticky top-24 transition-all duration-300 
                        ${isSidebarOpen ? 'w-72 opacity-100 mr-4' : 'w-0 opacity-0 p-0 mr-0 overflow-hidden'}
                        ${theme === 'dark' ? 'bg-neutral-800 text-white' : 'bg-white text-gray-900'}`}
          >
            {isSidebarOpen && (
              <>
                <h3 className="text-lg font-bold mb-2">{t.filtersTitle}</h3>

                {/* ColecciÃ³n */}
                <div>
                  <label className="block font-semibold mb-1">{t.collectionLabel}</label>
                  <select
                    value={collection}
                    onChange={e => { setCollection(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 border"
                  >
                    {collections.map(col => (
                      <option key={col} value={col}>{col.replace('-', ' ').toUpperCase()}</option>
                    ))}
                  </select>
                </div>

                {/* CategorÃ­a */}
                <div>
                  <label className="block font-semibold mb-1">{t.categoryLabel}</label>
                  <select
                    value={category}
                    onChange={e => { setCategory(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 border"
                  >
                    {categories.map(cat => (
                      <option key={cat} value={cat}>{cat.charAt(0).toUpperCase() + cat.slice(1)}</option>
                    ))}
                  </select>
                </div>

                {/* GÃ©nero */}
                <div>
                  <label className="block font-semibold mb-1">{t.genderLabel}</label>
                  <select
                    value={gender}
                    onChange={e => { setGender(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 border"
                  >
                    <option value="all">{t.allGenders}</option>
                    <option value="men">{t.men}</option>
                    <option value="women">{t.women}</option>
                  </select>
                </div>

                {/* Ordenar */}
                <div>
                  <label className="block font-semibold mb-1">{t.sortLabel}</label>
                  <select
                    value={sort}
                    onChange={e => setSort(e.target.value)}
                    className="w-full px-3 py-2 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 border"
                  >
                    <option value="default">{t.sortDefault}</option>
                    <option value="price-asc">{t.sortPriceAsc}</option>
                    <option value="price-desc">{t.sortPriceDesc}</option>
                    <option value="name">{t.sortName}</option>
                  </select>
                </div>

                {/* Precio */}
                <div>
                  <label className="block font-semibold mb-1">{t.priceLabel}</label>
                  <input
                    type="range"
                    min="0"
                    max="200"
                    step="10"
                    value={priceRange[1]}
                    onChange={e => setPriceRange([0, Number(e.target.value)])}
                    className="w-full accent-amber-500 cursor-pointer"
                  />
                  <p className="text-sm mt-1">{t.upTo} ${priceRange[1].toFixed(2)}</p>
                </div>
              </>
            )}
          </aside>

          {/* Productos */}
          <div className="flex-1">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.4 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product, index) => (
                    <ProductCard key={product.id} product={product} index={index} />
                  ))
                ) : (
                  <p className="text-center text-lg col-span-full">{t.noProducts}</p>
                )}
              </motion.div>
            </AnimatePresence>

            {/* PaginaciÃ³n */}
            {totalPages > 1 && (
              <div className="flex justify-center items-center gap-2 mb-12 flex-wrap">
                <button onClick={() => handlePageChange(currentPage - 1)} className="p-2 rounded-full hover:bg-amber-200 transition" disabled={currentPage === 1}>
                  <ArrowLeft size={20} />
                </button>
                {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                  <button
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`px-4 py-2 rounded-full border transition-all ${
                      currentPage === page
                        ? 'bg-amber-400 text-gray-900 border-amber-400'
                        : 'bg-white text-stone-800 border-stone-300 hover:bg-amber-200'
                    }`}
                  >
                    {page}
                  </button>
                ))}
                <button onClick={() => handlePageChange(currentPage + 1)} className="p-2 rounded-full hover:bg-amber-200 transition" disabled={currentPage === totalPages}>
                  <ArrowRight size={20} />
                </button>
              </div>
            )}

            {/* BotÃ³n final */}
            <div className="text-center">
              <button className="group bg-gradient-to-r from-amber-400 to-amber-300 text-gray-900 px-12 py-5 rounded-full font-bold text-lg hover:from-amber-500 hover:to-amber-400 hover:shadow-lg transform hover:scale-105 transition-all duration-300 flex items-center gap-3 mx-auto">
                {t.viewAllProducts} <ArrowRight className="group-hover:translate-x-2 transition-transform duration-300" size={24} />
              </button>
            </div>
          </div>
        </div>

        {/* Panel filtros mÃ³viles */}
        <div className={`fixed inset-0 bg-black/40 z-50 flex justify-end transition-opacity duration-300 ${isFilterOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'}`}>
          <aside
            className={`bg-white w-72 h-full p-6 shadow-lg overflow-y-auto relative transform transition-transform duration-300 ease-in-out
              ${isFilterOpen ? 'translate-x-0' : 'translate-x-full'}
              ${theme === 'dark' ? 'bg-neutral-800 text-white' : 'bg-white text-gray-900'}`}
          >
            <button
              onClick={() => setIsFilterOpen(false)}
              className="absolute top-4 right-4 hover:text-amber-500 transition-colors"
            >
              <X size={28} />
            </button>
            <h3 className="text-lg font-bold mb-4">{t.filtersTitle}</h3>
            {/* Repetir campos de filtro como en sidebar desktop */}
            {/* ... */}
          </aside>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
