import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';

const Header = ({ setCurrentPage, currentPage, scrollToProducts, theme = 'light' }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' },
  ];

  const handleNavClick = (page) => {
    if (page === 'products') {
      if (currentPage !== 'home') {
        setCurrentPage('home');
        setTimeout(() => scrollToProducts(), 100);
      } else {
        scrollToProducts();
      }
    } else {
      setCurrentPage(page);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled
          ? 'bg-white/90 backdrop-blur-md shadow-md py-2'
          : 'bg-white/60 backdrop-blur-sm py-4'
      }`}
    >
      <nav className="container mx-auto px-6">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <button
            onClick={() => handleNavClick('home')}
            className="flex items-center space-x-2 font-extrabold text-2xl tracking-tight hover:scale-105 transition-transform duration-300 text-gray-900"
          >
            <span className="text-amber-500">Mi</span>
            <span>Tienda</span>
          </button>

          {/* Desktop Nav */}
          <div className="hidden md:flex items-center space-x-10">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="relative font-medium text-lg transition-all duration-300 group text-gray-800 hover:text-amber-500"
              >
                {item.name}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-amber-400 group-hover:w-full transition-all duration-300 rounded"></span>
              </button>
            ))}
          </div>

          {/* Mobile Menu Button */}
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden transition-transform duration-300 hover:scale-110 text-gray-800 hover:text-amber-500"
          >
            {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
        </div>

        {/* Mobile Menu Animado */}
        <div
          className={`md:hidden fixed inset-0 z-50 flex items-center justify-center bg-black/20 backdrop-blur-sm transition-opacity duration-300 ease-in-out ${
            isMenuOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
        >
          <div
            className={`bg-gradient-to-br from-blue-200 via-blue-100 to-amber-100 rounded-3xl w-5/6 max-w-sm p-8 flex flex-col items-center space-y-6 transform transition-transform duration-500 ease-in-out ${
              isMenuOpen ? 'translate-y-0' : '-translate-y-full'
            } shadow-xl`}
          >
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="text-2xl font-semibold transition-all duration-300 hover:scale-105 text-gray-900 hover:text-amber-500"
              >
                {item.name}
              </button>
            ))}
            {/* Cerrar menú */}
            <button
              onClick={() => setIsMenuOpen(false)}
              className="absolute top-6 right-6 text-gray-900 hover:text-amber-500 transition-colors"
            >
              <X size={28} />
            </button>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
