import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Sparkles,
  ArrowRight,
  ArrowLeft,
  Filter,
  X,
  ChevronLeft,
  ChevronRight,
  Tag,
  Layers,
  Zap,
  Grid3x3,
  SlidersHorizontal,
} from 'lucide-react';

// Simulación de datos y contexto
const products = Array.from({ length: 24 }, (_, i) => ({
  id: i + 1,
  name: `Producto ${i + 1}`,
  category: ['camisetas', 'pantalones', 'zapatos'][i % 3],
  gender: ['men', 'women'][i % 2],
  price: Math.floor(Math.random() * 150) + 50,
  isNew: i < 8,
  isBestseller: i % 3 === 0,
  isOnSale: i % 4 === 0,
  image: `https://picsum.photos/400/500?random=${i}`,
}));

const useTheme = () => {
  const [theme, setTheme] = useState('light');
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);
  return { theme };
};

const ProductCard = ({ product }) => (
  <div className="bg-white dark:bg-gray-800 rounded-2xl overflow-hidden shadow-lg hover:shadow-2xl transition-all duration-300 hover:-translate-y-1">
    <div className="aspect-[4/5] bg-gradient-to-br from-amber-100 to-orange-100 dark:from-gray-700 dark:to-gray-600" />
    <div className="p-5">
      <h3 className="font-bold text-lg text-gray-900 dark:text-white mb-2">{product.name}</h3>
      <p className="text-amber-600 dark:text-amber-400 font-bold text-xl">${product.price}</p>
    </div>
  </div>
);

const ProductList = () => {
  const { theme } = useTheme();

  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceMax, setPriceMax] = useState(200);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  const containerRef = useRef(null);
  const productsPerPage = 6;
  const categories = useMemo(() => ['all', ...new Set(products.map((p) => p.category))], []);
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];

  const filteredProducts = useMemo(() => {
    const base = products
      .filter((p) => (category === 'all' ? true : p.category === category))
      .filter((p) => (gender === 'all' ? true : p.gender === gender))
      .filter((p) => p.price <= priceMax)
      .filter((p) => {
        switch (collection) {
          case 'novedades':
          case 'nuevos':
            return p.isNew === true;
          case 'mas-vendidos':
            return p.isBestseller === true;
          case 'ofertas':
            return p.isOnSale === true;
          default:
            return true;
        }
      });

    base.sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

    return base;
  }, [category, gender, collection, priceMax, sort]);

  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / productsPerPage));
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  useEffect(() => setCurrentPage(1), [category, collection, gender, priceMax, sort]);

  const FilterSection = ({ isMobile = false }) => (
    <div className="space-y-6">
      {/* Header de filtros */}
      <div className="flex items-center justify-between pb-4 border-b border-gray-200 dark:border-gray-700">
        <div className="flex items-center gap-3">
          <div className="p-2 rounded-lg bg-amber-100 dark:bg-amber-900/30">
            <SlidersHorizontal size={20} className="text-amber-600 dark:text-amber-400" />
          </div>
          <h3 className="text-xl font-bold text-gray-900 dark:text-white">Filtros</h3>
        </div>
        {isMobile && (
          <button
            onClick={() => setIsFilterOpen(false)}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
            aria-label="Cerrar filtros"
          >
            <X size={24} className="text-gray-600 dark:text-gray-300" />
          </button>
        )}
      </div>

      {/* Colección */}
      <div className="space-y-3">
        <label className="flex items-center gap-2 text-sm font-bold text-gray-700 dark:text-gray-300 uppercase tracking-wide">
          <Sparkles size={16} className="text-amber-500" />
          Colección
        </label>
        <div className="grid grid-cols-2 gap-2">
          {collections.map((col) => (
            <button
              key={col}
              onClick={() => { setCollection(col); setCurrentPage(1); }}
              className={`px-4 py-3 rounded-xl text-sm font-semibold transition-all duration-300 ${
                collection === col
                  ? 'bg-gradient-to-r from-amber-500 to-orange-500 text-white shadow-lg scale-105'
                  : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600'
              }`}
            >
              {col === 'all' ? 'Todas' : col.replace('-', ' ')}
            </button>
          ))}
        </div>
      </div>

      {/* Categoría */}
      <div className="space-y-3">
        <label className="flex items-center gap-2 text-sm font-bold text-gray-700 dark:text-gray-300 uppercase tracking-wide">
          <Grid3x3 size={16} className="text-amber-500" />
          Categoría
        </label>
        <div className="space-y-2">
          {categories.map((cat) => (
            <button
              key={cat}
              onClick={() => { setCategory(cat); setCurrentPage(1); }}
              className={`w-full px-4 py-3 rounded-xl text-left font-semibold transition-all duration-300 ${
                category === cat
                  ? 'bg-amber-500 text-white shadow-md'
                  : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-amber-50 dark:hover:bg-gray-700 border border-gray-200 dark:border-gray-600'
              }`}
            >
              {cat === 'all' ? 'Todas las categorías' : cat.charAt(0).toUpperCase() + cat.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Género */}
      <div className="space-y-3">
        <label className="flex items-center gap-2 text-sm font-bold text-gray-700 dark:text-gray-300 uppercase tracking-wide">
          <Zap size={16} className="text-amber-500" />
          Género
        </label>
        <div className="grid grid-cols-3 gap-2">
          {[
            { value: 'all', label: 'Todos' },
            { value: 'men', label: 'Hombre' },
            { value: 'women', label: 'Mujer' },
          ].map((g) => (
            <button
              key={g.value}
              onClick={() => { setGender(g.value); setCurrentPage(1); }}
              className={`px-4 py-3 rounded-xl text-sm font-semibold transition-all duration-300 ${
                gender === g.value
                  ? 'bg-amber-500 text-white shadow-md'
                  : 'bg-gray-100 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-600'
              }`}
            >
              {g.label}
            </button>
          ))}
        </div>
      </div>

      {/* Ordenar */}
      <div className="space-y-3">
        <label className="flex items-center gap-2 text-sm font-bold text-gray-700 dark:text-gray-300 uppercase tracking-wide">
          <Layers size={16} className="text-amber-500" />
          Ordenar por
        </label>
        <select
          value={sort}
          onChange={(e) => setSort(e.target.value)}
          className="w-full px-4 py-3 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-xl focus:outline-none focus:ring-2 focus:ring-amber-500 text-gray-700 dark:text-gray-300 font-medium"
        >
          <option value="default">Predeterminado</option>
          <option value="price-asc">Precio: Menor a Mayor</option>
          <option value="price-desc">Precio: Mayor a Menor</option>
          <option value="name">Nombre A-Z</option>
        </select>
      </div>

      {/* Precio */}
      <div className="space-y-3">
        <label className="flex items-center gap-2 text-sm font-bold text-gray-700 dark:text-gray-300 uppercase tracking-wide">
          <Tag size={16} className="text-amber-500" />
          Rango de Precio
        </label>
        <div className="bg-gradient-to-r from-amber-50 to-orange-50 dark:from-gray-800 dark:to-gray-700 p-4 rounded-xl">
          <input
            type="range"
            min="0"
            max="500"
            step="5"
            value={priceMax}
            onChange={(e) => setPriceMax(Number(e.target.value))}
            className="w-full h-2 rounded-lg appearance-none cursor-pointer bg-gray-200 dark:bg-gray-600"
            style={{
              background: `linear-gradient(to right, rgb(245, 158, 11) 0%, rgb(245, 158, 11) ${(priceMax / 500) * 100}%, rgb(229, 231, 235) ${(priceMax / 500) * 100}%, rgb(229, 231, 235) 100%)`
            }}
          />
          <div className="flex justify-between items-center mt-3">
            <span className="text-sm text-gray-600 dark:text-gray-400">$0</span>
            <span className="text-lg font-bold text-amber-600 dark:text-amber-400">${priceMax}</span>
            <span className="text-sm text-gray-600 dark:text-gray-400">$500</span>
          </div>
        </div>
      </div>

      {/* Botón limpiar filtros */}
      <button
        onClick={() => {
          setCategory('all');
          setCollection('all');
          setGender('all');
          setSort('default');
          setPriceMax(200);
          setCurrentPage(1);
        }}
        className="w-full py-3 rounded-xl border-2 border-gray-300 dark:border-gray-600 text-gray-700 dark:text-gray-300 font-semibold hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
      >
        Limpiar Filtros
      </button>
    </div>
  );

  return (
    <section className="relative transition-colors duration-500 ease-in-out bg-gradient-to-b from-amber-50 via-white to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 py-20">
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Header */}
        <div className="text-center mb-12">
          <div className="inline-flex items-center gap-2 bg-amber-100 dark:bg-amber-900/30 px-6 py-2 rounded-full mb-4 shadow-sm">
            <Sparkles size={18} className="text-amber-500 dark:text-amber-400" />
            <span className="text-sm font-bold text-gray-800 dark:text-gray-200">Colección Exclusiva</span>
          </div>
          <h2 className="text-4xl md:text-5xl font-extrabold text-gray-900 dark:text-white mb-3">
            Nuestra Colección
          </h2>
          <p className="text-md md:text-lg text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Moda urbana con un toque elegante — seleccionada para quienes buscan estilo y comodidad.
          </p>
        </div>

        {/* Barra superior */}
        <div className="flex items-center justify-between mb-8 gap-4">
          <div className="flex items-center gap-3 bg-white dark:bg-gray-800 px-4 py-2 rounded-xl shadow-sm">
            <Tag size={18} className="text-amber-500" />
            <span className="text-sm font-bold text-gray-700 dark:text-gray-300">
              {filteredProducts.length} productos
            </span>
          </div>

          <button
            onClick={() => setIsFilterOpen(true)}
            className="md:hidden flex items-center gap-2 bg-gradient-to-r from-amber-500 to-orange-500 text-white px-6 py-3 rounded-xl font-bold shadow-lg hover:shadow-xl hover:scale-105 transition-all duration-300"
            aria-label="Abrir filtros"
          >
            <Filter size={18} /> Filtros
          </button>
        </div>

        <div className="flex gap-6 relative">
          {/* Toggle sidebar desktop */}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className={`hidden md:flex fixed z-10 items-center justify-center bg-amber-500 text-white p-3 rounded-full shadow-lg hover:shadow-xl hover:scale-110 transition-all duration-300 ${
              isSidebarOpen ? 'left-[340px]' : 'left-4'
            }`}
            style={{ top: '140px' }}
            aria-label="Mostrar/ocultar filtros"
          >
            {isSidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
          </button>

          {/* Sidebar desktop */}
          <aside
            className={`hidden md:block bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-xl transition-all duration-500 ${
              isSidebarOpen ? 'w-80 opacity-100' : 'w-0 opacity-0 p-0 overflow-hidden'
            }`}
            style={{ maxHeight: 'calc(100vh - 200px)', overflowY: 'auto' }}
          >
            {isSidebarOpen && <FilterSection />}
          </aside>

          {/* Grid de productos */}
          <div className="flex-1">
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {currentProducts.length > 0 ? (
                currentProducts.map((product) => (
                  <ProductCard key={product.id} product={product} />
                ))
              ) : (
                <p className="text-center text-gray-600 dark:text-gray-300 text-lg col-span-full py-12">
                  No se encontraron productos que coincidan con los filtros.
                </p>
              )}
            </div>

            {/* Paginación */}
            {totalPages > 1 && (
              <div className="flex justify-center items-center gap-2 mb-8 flex-wrap">
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  className="p-3 rounded-xl hover:bg-amber-100 dark:hover:bg-gray-700 transition disabled:opacity-50"
                  disabled={currentPage === 1}
                  aria-label="Anterior"
                >
                  <ArrowLeft size={20} />
                </button>

                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <button
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`px-5 py-3 rounded-xl font-bold transition-all duration-300 ${
                      currentPage === page
                        ? 'bg-gradient-to-r from-amber-500 to-orange-500 text-white shadow-lg scale-110'
                        : 'bg-white dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-amber-50 dark:hover:bg-gray-600'
                    }`}
                  >
                    {page}
                  </button>
                ))}

                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  className="p-3 rounded-xl hover:bg-amber-100 dark:hover:bg-gray-700 transition disabled:opacity-50"
                  disabled={currentPage === totalPages}
                  aria-label="Siguiente"
                >
                  <ArrowRight size={20} />
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Modal filtros móvil */}
        <div
          className={`md:hidden fixed inset-0 bg-black/60 backdrop-blur-sm z-50 transition-opacity duration-300 ${
            isFilterOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
          onClick={() => setIsFilterOpen(false)}
        >
          <aside
            className={`absolute right-0 top-0 h-full w-full max-w-md bg-white dark:bg-gray-900 p-6 overflow-y-auto transition-transform duration-300 ${
              isFilterOpen ? 'translate-x-0' : 'translate-x-full'
            }`}
            onClick={(e) => e.stopPropagation()}
          >
            <FilterSection isMobile />
          </aside>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
