import React from 'react';

const Novedades = () => {
  const novedades = [
    {
      id: 1,
      title: 'Campera urbana 2025',
      category: 'Hombre',
      description: 'Ligera y cómoda, ideal para la ciudad.',
      image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 2,
      title: 'Vestido verano 2025',
      category: 'Mujer',
      description: 'Fresco y elegante para días soleados.',
      image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 3,
      title: 'Remera clásica unisex',
      category: 'Unisex',
      description: 'Básica y versátil para cualquier ocasión.',
      image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 4,
      title: 'Pantalón cargo',
      category: 'Hombre',
      description: 'Cómodo y funcional, con bolsillos amplios.',
      image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=800&q=80',
    },
  ];

  return (
    <section className="py-16" style={{ backgroundColor: '#fef3c7' }}>
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold mb-12 text-center text-gray-900">Novedades</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
          {novedades.map((item) => (
            <div
              key={item.id}
              className="bg-white rounded-xl shadow hover:shadow-lg overflow-hidden transition-transform transform hover:scale-105"
            >
              <img
                src={item.image}
                alt={item.title}
                className="w-full h-48 object-cover"
              />
              <div className="p-5">
                <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
                <p className="text-sm text-gray-500 mb-2">{item.category}</p>
                <p className="text-gray-600">{item.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Novedades;
