import React, { useState } from 'react';
import { MapPin, Phone, Mail, Sparkles, Send } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [ref, isVisible] = useIntersectionObserver();

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('¡Gracias por tu mensaje! Te responderemos pronto.');
    setFormData({ name: '', email: '', subject: '', message: '' });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <section className="py-20 bg-gradient-to-b from-purple-50 to-pink-50 relative overflow-hidden">
      {/* Fondos flotantes */}
      <div className="absolute top-20 left-10 w-72 h-72 bg-purple-200/30 rounded-full blur-3xl animate-float"></div>
      <div className="absolute bottom-20 right-10 w-72 h-72 bg-pink-200/30 rounded-full blur-3xl animate-float-delayed"></div>
      
      <div className="container mx-auto px-4 relative z-10">
        {/* Título */}
        <div ref={ref} className={`text-center mb-16 ${isVisible ? 'animate-fade-in-up' : 'opacity-0'}`}>
          <h2 className="text-5xl md:text-6xl font-bold text-purple-800 mb-6">
            Contáctanos
          </h2>
          <p className="text-xl md:text-2xl text-purple-700 max-w-2xl mx-auto">
            ¿Tienes alguna pregunta? Estamos aquí para ayudarte
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-12">
          {/* Información de contacto */}
          <div className="space-y-6">
            {[
              { icon: MapPin, title: 'Ubicación', text: 'Av. Principal 1234, Buenos Aires, Argentina', color: 'purple' },
              { icon: Phone, title: 'Teléfono', text: '+54 11 1234-5678', color: 'pink' },
              { icon: Mail, title: 'Email', text: 'contacto@stylehub.com', color: 'orange' }
            ].map((item, i) => (
              <div key={i} className={`flex items-start gap-4 p-6 bg-${item.color}-50 rounded-2xl hover:shadow-lg transform hover:-translate-y-1 transition-all duration-300 ${isVisible ? 'animate-slide-in-left' : 'opacity-0'}`}>
                <div className={`bg-gradient-to-br from-${item.color}-300 to-${item.color}-400 p-3 rounded-xl shadow-lg`}>
                  <item.icon className="text-white" size={24} />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-purple-800 mb-2">{item.title}</h3>
                  <p className="text-purple-700">{item.text}</p>
                </div>
              </div>
            ))}

            {/* Horarios */}
            <div className={`p-6 bg-gradient-to-br from-purple-200 via-pink-200 to-orange-200 rounded-2xl text-purple-800 shadow-lg ${isVisible ? 'animate-slide-in-left' : 'opacity-0'}`}>
              <h3 className="text-2xl font-bold mb-4 flex items-center gap-2">
                <Sparkles size={24} />
                Horarios de Atención
              </h3>
              <div className="space-y-2 text-lg">
                <p>Lunes a Viernes: 9:00 - 20:00</p>
                <p>Sábados: 10:00 - 18:00</p>
                <p>Domingos: Cerrado</p>
              </div>
            </div>
          </div>

          {/* Formulario */}
          <div className={`space-y-6 ${isVisible ? 'animate-slide-in-right' : 'opacity-0'}`}>
            {[
              { name: 'name', label: 'Nombre', type: 'text', placeholder: 'Tu nombre' },
              { name: 'email', label: 'Email', type: 'email', placeholder: 'tu@email.com' },
              { name: 'subject', label: 'Asunto', type: 'text', placeholder: 'Asunto del mensaje' }
            ].map((field, i) => (
              <div key={i}>
                <label className="block text-purple-800 font-bold mb-2">{field.label}</label>
                <input
                  type={field.type}
                  name={field.name}
                  value={formData[field.name]}
                  onChange={handleChange}
                  className="w-full px-5 py-4 border-2 border-purple-200 rounded-xl focus:ring-2 focus:ring-purple-400 focus:border-transparent transition-all outline-none hover:border-purple-300"
                  placeholder={field.placeholder}
                />
              </div>
            ))}

            <div>
              <label className="block text-purple-800 font-bold mb-2">Mensaje</label>
              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                rows="5"
                className="w-full px-5 py-4 border-2 border-purple-200 rounded-xl focus:ring-2 focus:ring-purple-400 focus:border-transparent transition-all outline-none resize-none hover:border-purple-300"
                placeholder="Escribe tu mensaje aquí..."
              ></textarea>
            </div>

            <button
              onClick={handleSubmit}
              className="w-full bg-gradient-to-r from-purple-200 via-pink-200 to-orange-200 text-purple-800 py-5 rounded-xl font-bold text-lg hover:shadow-lg transform hover:scale-105 transition-all duration-300 flex items-center justify-center gap-2"
            >
              <Send size={20} />
              Enviar Mensaje
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
