import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Sparkles,
  ArrowRight,
  ArrowLeft,
  Filter,
  X,
  ChevronLeft,
  ChevronRight,
  Tag,
  Layers,
  Zap,
  TrendingUp,
  DollarSign,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';
import { useScrollReveal } from '../hooks/useScrollReveal';

const ProductList = () => {
  const { theme } = useTheme();

  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceMax, setPriceMax] = useState(200);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const containerRef = useRef(null);

  // Scroll reveal animations
  const { ref: headerRef, controls: headerAnim } = useScrollReveal();
  const { ref: gridRef, controls: gridAnim } = useScrollReveal();
  const { ref: sidebarRef, controls: sidebarAnim } = useScrollReveal();

  const productsPerPage = 6;
  const categories = useMemo(() => ['all', ...new Set(products.map((p) => p.category))], []);
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];

  const filteredProducts = useMemo(() => {
    const base = products
      .filter((p) => (category === 'all' ? true : p.category === category))
      .filter((p) => (gender === 'all' ? true : p.gender === gender))
      .filter((p) => p.price <= priceMax)
      .filter((p) => {
        switch (collection) {
          case 'novedades':
          case 'nuevos':
            return p.isNew === true;
          case 'mas-vendidos':
            return p.isBestseller === true;
          case 'ofertas':
            return p.isOnSale === true;
          default:
            return true;
        }
      });

    base.sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

    return base;
  }, [category, gender, collection, priceMax, sort]);

  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / productsPerPage));
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage, theme]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  useEffect(() => setCurrentPage(1), [category, collection, gender, priceMax, sort]);

  return (
    <section
      key={theme}
      className="relative transition-colors duration-500 ease-in-out bg-gradient-to-b from-amber-100 via-white to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 py-20"
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Header animado */}
        <motion.div
          ref={headerRef}
          animate={headerAnim}
          initial={{ opacity: 0, y: 40 }}
          className="text-center mb-10"
        >
          <div className="inline-flex items-center gap-2 bg-amber-200 dark:bg-gray-800 px-6 py-2 rounded-full mb-4 shadow-sm">
            <Sparkles size={18} className="text-amber-500 dark:text-amber-300" />
            <span className="text-sm font-bold text-stone-800 dark:text-gray-200">Colección Exclusiva</span>
          </div>

          <h2 className="text-4xl md:text-5xl font-extrabold text-stone-900 dark:text-white mb-2">
            Nuestra Colección
          </h2>
          <p className="text-md md:text-lg text-stone-600 dark:text-gray-300 max-w-3xl mx-auto leading-relaxed">
            Moda urbana con un toque elegante — seleccionada para quienes buscan estilo y comodidad.
          </p>
        </motion.div>

        <div className="flex gap-8 relative">
          {/* Sidebar animado */}
          <motion.aside
            ref={sidebarRef}
            animate={sidebarAnim}
            initial={{ opacity: 0, y: 60 }}
            className={`hidden md:flex flex-col rounded-3xl p-6 shadow-[0_8px_30px_rgba(0,0,0,0.08)] space-y-6 max-h-[calc(100vh-220px)] overflow-y-auto sticky top-24 transition-all duration-500 backdrop-blur-md border border-white/20 dark:border-gray-700/40
              ${isSidebarOpen ? 'w-72 opacity-100 mr-6 bg-white/70 dark:bg-gray-800/70' : 'w-0 opacity-0 p-0 mr-0 overflow-hidden'}`}
          >
            {isSidebarOpen && (
              <>
                <div className="flex items-center justify-between mb-2">
                  <h3 className="text-lg font-bold text-stone-800 dark:text-gray-100 tracking-tight">Filtros</h3>
                  <Filter size={18} className="text-amber-500 dark:text-amber-400" />
                </div>

                {/* Filtros */}
                <label className="text-sm font-semibold text-stone-700 dark:text-gray-300">Colección</label>
                <select
                  value={collection}
                  onChange={(e) => setCollection(e.target.value)}
                  className="w-full px-4 py-2 border rounded-xl bg-white/70 dark:bg-gray-700/70"
                >
                  {collections.map((opt) => (
                    <option key={opt} value={opt}>
                      {opt.toUpperCase()}
                    </option>
                  ))}
                </select>

                <label className="text-sm font-semibold text-stone-700 dark:text-gray-300">Categoría</label>
                <select
                  value={category}
                  onChange={(e) => setCategory(e.target.value)}
                  className="w-full px-4 py-2 border rounded-xl bg-white/70 dark:bg-gray-700/70"
                >
                  {categories.map((opt) => (
                    <option key={opt} value={opt}>
                      {opt.toUpperCase()}
                    </option>
                  ))}
                </select>

                <label className="text-sm font-semibold text-stone-700 dark:text-gray-300">Género</label>
                <select
                  value={gender}
                  onChange={(e) => setGender(e.target.value)}
                  className="w-full px-4 py-2 border rounded-xl bg-white/70 dark:bg-gray-700/70"
                >
                  <option value="all">Todos</option>
                  <option value="men">Hombre</option>
                  <option value="women">Mujer</option>
                </select>

                <label className="text-sm font-semibold text-stone-700 dark:text-gray-300">Precio máximo</label>
                <input
                  type="range"
                  min="0"
                  max="500"
                  step="5"
                  value={priceMax}
                  onChange={(e) => setPriceMax(Number(e.target.value))}
                  className="w-full accent-amber-500"
                />
                <div className="text-sm text-stone-600 dark:text-gray-300">Hasta ${priceMax}</div>
              </>
            )}
          </motion.aside>

          {/* Grid animado */}
          <motion.div
            ref={gridRef}
            animate={gridAnim}
            initial={{ opacity: 0, y: 60 }}
            className="flex-1"
          >
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage + '-' + theme}
                initial={{ opacity: 0, y: 12 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -12 }}
                transition={{ duration: 0.45 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product, index) => (
                    <motion.div
                      key={product.id}
                      initial={{ opacity: 0, scale: 0.98 }}
                      animate={{ opacity: 1, scale: 1 }}
                      transition={{ duration: 0.4, delay: index * 0.05 }}
                    >
                      <ProductCard product={product} />
                    </motion.div>
                  ))
                ) : (
                  <p className="text-center text-stone-600 dark:text-gray-300 text-lg col-span-full">
                    No se encontraron productos que coincidan con los filtros.
                  </p>
                )}
              </motion.div>
            </AnimatePresence>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
