import React from 'react';
import { MapPin, Phone, Mail, Sparkles } from 'lucide-react';
import { useTheme } from '../context/ThemeContext';

const Contact = () => {
  const { theme } = useTheme();
  const isDark = theme === 'dark';

  return (
    <section
      className={`py-24 relative overflow-hidden transition-colors duration-500 ${
        isDark ? 'bg-gray-900' : 'bg-white'
      }`}
    >
      {/* Fondos decorativos */}
      <div
        className={`absolute top-36 right-16 w-56 h-56 rounded-full blur-2xl animate-float ${
          isDark ? 'bg-amber-400/30' : 'bg-amber-300/50'
        }`}
      ></div>
      <div
        className={`absolute bottom-16 left-10 w-64 h-64 rounded-full blur-3xl animate-float-delayed ${
          isDark ? 'bg-gray-700/25' : 'bg-gray-200/30'
        }`}
      ></div>

      <div className="container mx-auto px-6 relative z-10">
        {/* Título */}
        <div className="text-center mb-20">
          <h2
            className={`text-5xl md:text-6xl font-extrabold mb-4 tracking-tight ${
              isDark ? 'text-white' : 'text-gray-900'
            }`}
          >
            Contáctanos
          </h2>
          <p
            className={`text-lg md:text-xl max-w-2xl mx-auto ${
              isDark ? 'text-gray-300' : 'text-gray-600'
            }`}
          >
            Estamos para ayudarte — escribinos o visitanos cuando quieras.
          </p>
        </div>

        {/* Contenido principal */}
        <div className="grid lg:grid-cols-2 gap-12 items-stretch">
          {/* Formulario */}
          <form
            className={`p-10 rounded-3xl shadow-lg backdrop-blur-sm flex flex-col justify-between border hover:shadow-2xl transition-all duration-300 h-[600px] ${
              isDark ? 'bg-gray-800/60 border-gray-700 text-white' : 'bg-gray-50/80 border-gray-100 text-gray-900'
            }`}
          >
            <div className="flex flex-col gap-6">
              <h3 className={`text-2xl font-bold mb-2 ${isDark ? 'text-white' : 'text-gray-900'}`}>
                Envíanos un mensaje
              </h3>
              <input
                type="text"
                placeholder="Tu nombre"
                className={`p-4 rounded-xl border focus:outline-none focus:ring-2 transition ${
                  isDark
                    ? 'bg-gray-700 border-gray-600 text-white focus:ring-amber-500'
                    : 'bg-white border-gray-300 focus:ring-amber-500 text-gray-900'
                }`}
              />
              <input
                type="email"
                placeholder="Tu correo electrónico"
                className={`p-4 rounded-xl border focus:outline-none focus:ring-2 transition ${
                  isDark
                    ? 'bg-gray-700 border-gray-600 text-white focus:ring-amber-500'
                    : 'bg-white border-gray-300 focus:ring-amber-500 text-gray-900'
                }`}
              />
              <textarea
                placeholder="Tu mensaje"
                rows="5"
                className={`p-4 rounded-xl border focus:outline-none focus:ring-2 transition resize-none ${
                  isDark
                    ? 'bg-gray-700 border-gray-600 text-white focus:ring-amber-500'
                    : 'bg-white border-gray-300 focus:ring-amber-500 text-gray-900'
                }`}
              />
            </div>
            <button
              type="submit"
              className={`font-semibold py-4 rounded-xl transition-all shadow-md hover:shadow-lg ${
                isDark ? 'bg-amber-500 hover:bg-amber-600 text-gray-900' : 'bg-amber-500 hover:bg-amber-600 text-white'
              }`}
            >
              Enviar mensaje
            </button>
          </form>

          {/* Mapa + Info */}
          <div className="relative w-full h-[600px] rounded-3xl overflow-hidden shadow-xl border flex flex-col-reverse lg:flex-col">
            {/* Tarjeta de información */}
            <div
              className={`absolute lg:static top-0 right-0 p-6 rounded-2xl shadow-2xl w-full lg:w-80 border backdrop-blur-md mb-6 lg:mb-0 ${
                isDark ? 'bg-gray-800/70 border-gray-700 text-white' : 'bg-white/95 border-gray-100 text-gray-700'
              }`}
            >
              <h4 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <Sparkles className="text-amber-600" size={22} />
                Información de contacto
              </h4>
              <div className="flex flex-col gap-4">
                <div className="flex items-center gap-3">
                  <MapPin className="text-amber-600" size={22} />
                  <p>Av. Principal 1234, Buenos Aires</p>
                </div>
                <div className="flex items-center gap-3">
                  <Phone className="text-amber-600" size={22} />
                  <p>+54 11 1234-5678</p>
                </div>
                <div className="flex items-center gap-3">
                  <Mail className="text-amber-600" size={22} />
                  <p>contacto@MiTienda.com</p>
                </div>
              </div>

              <div
                className={`mt-6 p-4 rounded-xl text-center border ${
                  isDark ? 'bg-gray-700/50 border-gray-700 text-white' : 'bg-gray-50 border-gray-200 text-gray-800'
                }`}
              >
                <h5 className="font-semibold mb-1 flex justify-center items-center gap-2">
                  <Sparkles className="text-amber-600" size={18} />
                  Horarios de atención
                </h5>
                <p className={`${isDark ? 'text-gray-300' : 'text-gray-600'} text-sm`}>Lunes a Viernes: 9:00 - 20:00</p>
                <p className={`${isDark ? 'text-gray-300' : 'text-gray-600'} text-sm`}>Sábados: 10:00 - 18:00</p>
                <p className={`${isDark ? 'text-gray-300' : 'text-gray-600'} text-sm`}>Domingos: Cerrado</p>
              </div>
            </div>

            <iframe
              title="Ubicación"
              src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3280.851029885265!2d-58.38159248477037!3d-34.603684580459616!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x95bccac22db1b7b9%3A0xc0c8d3f2ef4375aa!2sAv.%20Principal%201234%2C%20Buenos%20Aires!5e0!3m2!1ses-419!2sar!4v1698519123456!5m2!1ses-419!2sar"
              className="w-full h-full border-0"
              allowFullScreen
              loading="lazy"
              referrerPolicy="no-referrer-when-downgrade"
            ></iframe>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
