import React, { useState } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import ProductList from './components/ProductList';
import Contact from './components/Contact';
import Footer from './components/Footer';

// Componente de Novedades
const Novedades = () => {
  // Aquí podrías traer novedades desde un API o un array
  const novedades = [
    { id: 1, title: 'Nueva colección otoño 2025', description: 'Prendas exclusivas y limitadas.' },
    { id: 2, title: 'Oferta especial', description: 'Descuentos por tiempo limitado.' },
    { id: 3, title: 'Lanzamiento de accesorios', description: 'Complementa tu estilo.' },
  ];

  return (
    <section className="py-16 bg-gray-100">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold mb-8 text-center">Novedades</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {novedades.map((item) => (
            <div key={item.id} className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
              <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
              <p className="text-gray-600">{item.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

const Home = ({ scrollToProducts }) => {
  return (
    <>
      <Hero scrollToProducts={scrollToProducts} />
      <Novedades /> {/* Sección de novedades */}
      <ProductList />
      <Contact />
    </>
  );
};

const ContactPage = () => {
  return (
    <div className="pt-20">
      <Contact />
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');

  const scrollToProducts = () => {
    const element = document.getElementById('productos');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setCurrentPage('home'); // aseguramos que si estás en contacto, vuelva a Home
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header 
        setCurrentPage={setCurrentPage} 
        currentPage={currentPage} 
        scrollToProducts={scrollToProducts} 
      />
      <main className="flex-grow">
        {currentPage === 'home' && <Home scrollToProducts={scrollToProducts} />}
        {currentPage === 'contact' && <ContactPage />}
      </main>
      <Footer />
    </div>
  );
};

export default App;
