import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Sparkles,
  ArrowRight,
  ArrowLeft,
  Filter,
  X,
  ChevronLeft,
  ChevronRight,
  Tag,
  Layers,
  Zap,
  TrendingUp,
  DollarSign,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';

const ProductList = () => {
  const { theme } = useTheme();

  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceMax, setPriceMax] = useState(200);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  const containerRef = useRef(null);
  const productsPerPage = 6;
  const categories = useMemo(() => ['all', ...new Set(products.map((p) => p.category))], []);
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];

  const filteredProducts = useMemo(() => {
    const base = products
      .filter((p) => (category === 'all' ? true : p.category === category))
      .filter((p) => (gender === 'all' ? true : p.gender === gender))
      .filter((p) => p.price <= priceMax)
      .filter((p) => {
        switch (collection) {
          case 'novedades':
          case 'nuevos':
            return p.isNew === true;
          case 'mas-vendidos':
            return p.isBestseller === true;
          case 'ofertas':
            return p.isOnSale === true;
          default:
            return true;
        }
      });

    base.sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

    return base;
  }, [category, gender, collection, priceMax, sort]);

  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / productsPerPage));
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  // Scroll al top de los productos solo cuando cambia la pÃ¡gina
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  useEffect(() => setCurrentPage(1), [category, collection, gender, priceMax, sort]);

  return (
    <section
      key={theme}
      className="relative transition-colors duration-500 ease-in-out bg-gradient-to-b from-amber-100 via-white to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 py-20"
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        <div className="text-center mb-10">
          <div className="inline-flex items-center gap-2 bg-amber-200 dark:bg-gray-800 px-6 py-2 rounded-full mb-4 shadow-sm transition-colors duration-500">
            <Sparkles size={18} className="text-amber-500 dark:text-amber-300" />
            <span className="text-sm font-bold text-stone-800 dark:text-gray-200">ColecciÃ³n Exclusiva</span>
          </div>
          <h2 className="text-4xl md:text-5xl font-extrabold text-stone-900 dark:text-white mb-2">Nuestra ColecciÃ³n</h2>
          <p className="text-md md:text-lg text-stone-600 dark:text-gray-300 max-w-3xl mx-auto leading-relaxed">
            Moda urbana con un toque elegante â seleccionada para quienes buscan estilo y comodidad.
          </p>
        </div>

        <div className="flex items-center justify-between mb-6 gap-4">
          <div className="hidden md:flex items-center gap-3">
            <span className="text-sm text-stone-600 dark:text-gray-300">Filtros rÃ¡pidos:</span>
            <div className="flex gap-2">
              <button
                onClick={() => { setCollection('novedades'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
                title="Novedades"
              >
                Novedades
              </button>
              <button
                onClick={() => { setCollection('ofertas'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
                title="Ofertas"
              >
                Ofertas
              </button>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="hidden md:flex items-center gap-2 text-sm text-stone-600 dark:text-gray-300">
              <Tag size={16} /> <span>{filteredProducts.length} resultados</span>
            </div>

            <button
              onClick={() => setIsFilterOpen(true)}
              className="flex items-center gap-2 bg-amber-400 dark:bg-amber-600 text-gray-900 dark:text-gray-900 px-4 py-2 rounded-full font-semibold shadow hover:scale-105 transition-transform duration-300 md:hidden"
              aria-label="Abrir filtros"
            >
              <Filter size={18} /> Filtros
            </button>
          </div>
        </div>

        <div className="flex gap-8 relative">
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="hidden md:flex absolute -left-5 top-2 z-10 items-center justify-center bg-amber-400 dark:bg-amber-600 text-gray-900 dark:text-gray-900 p-2 rounded-full shadow hover:scale-105 transition-transform duration-300"
            aria-label="Mostrar/ocultar filtros"
          >
            {isSidebarOpen ? <ChevronLeft size={18} /> : <ChevronRight size={18} />}
          </button>

          <aside
            className={`hidden md:flex flex-col rounded-2xl p-6 shadow-md space-y-6 max-h-[calc(100vh-220px)] overflow-y-auto sticky top-24 transition-all duration-500 bg-white/80 dark:bg-gray-800/80 backdrop-blur-sm
              ${isSidebarOpen ? 'w-72 opacity-100 mr-4' : 'w-0 opacity-0 p-0 mr-0 overflow-hidden'}`}
            aria-hidden={!isSidebarOpen}
          >
            {isSidebarOpen && (
              <>
                <h3 className="text-lg font-bold text-stone-800 dark:text-gray-200 mb-1">Filtros</h3>
                <div className="space-y-2">
                  <label className="flex items-center gap-2 text-sm font-semibold text-stone-700 dark:text-gray-300">
                    <Layers size={16} /> ColecciÃ³n
                  </label>
                  <select
                    value={collection}
                    onChange={(e) => { setCollection(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                  >
                    {collections.map((col) => (
                      <option key={col} value={col}>
                        {col === 'all' ? 'TODAS' : col.replace('-', ' ').toUpperCase()}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="space-y-2">
                  <label className="flex items-center gap-2 text-sm font-semibold text-stone-700 dark:text-gray-300">
                    <Tag size={16} /> CategorÃ­a
                  </label>
                  <select
                    value={category}
                    onChange={(e) => { setCategory(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                  >
                    {categories.map((cat) => (
                      <option key={cat} value={cat}>
                        {cat === 'all' ? 'Todos' : cat.charAt(0).toUpperCase() + cat.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="space-y-2">
                  <label className="flex items-center gap-2 text-sm font-semibold text-stone-700 dark:text-gray-300">
                    <Zap size={16} /> GÃ©nero
                  </label>
                  <select
                    value={gender}
                    onChange={(e) => { setGender(e.target.value); setCurrentPage(1); }}
                    className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                  >
                    <option value="all">Todos</option>
                    <option value="men">Hombre</option>
                    <option value="women">Mujer</option>
                  </select>
                </div>
              </>
            )}
          </aside>

          <div className="flex-1">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage + '-' + theme}
                initial={{ opacity: 0, y: 12 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -12 }}
                transition={{ duration: 0.45 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product, index) => (
                    <motion.div
                      key={product.id}
                      initial={{ opacity: 0, scale: 0.98 }}
                      animate={{ opacity: 1, scale: 1 }}
                      exit={{ opacity: 0, scale: 0.98 }}
                      transition={{ duration: 0.35, delay: index * 0.03 }}
                    >
                      <ProductCard product={product} />
                    </motion.div>
                  ))
                ) : (
                  <p className="text-center text-stone-600 dark:text-gray-300 text-lg col-span-full">
                    No se encontraron productos que coincidan con los filtros.
                  </p>
                )}
              </motion.div>
            </AnimatePresence>

            {totalPages > 1 && (
              <div className="flex justify-center items-center gap-2 mb-8 flex-wrap">
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition"
                  disabled={currentPage === 1}
                  aria-label="Anterior"
                >
                  <ArrowLeft size={18} />
                </button>

                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <button
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`px-4 py-2 rounded-full border transition-all duration-200 ${
                      currentPage === page
                        ? 'bg-amber-400 text-gray-900 border-amber-400 dark:bg-amber-600 dark:text-gray-900 dark:border-amber-600'
                        : 'bg-white text-stone-800 border-stone-300 hover:bg-amber-200 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600 dark:hover:bg-amber-500'
                    }`}
                    aria-current={currentPage === page ? 'page' : undefined}
                  >
                    {page}
                  </button>
                ))}

                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition"
                  disabled={currentPage === totalPages}
                  aria-label="Siguiente"
                >
                  <ArrowRight size={18} />
                </button>
              </div>
            )}

            <div className="text-center">
              <button
                onClick={() => {
                  setCategory('all');
                  setCollection('all');
                  setGender('all');
                  setSort('default');
                  setPriceMax(500);
                  setCurrentPage(1);
                }}
                className="group inline-flex items-center gap-3 bg-gradient-to-r from-amber-400 to-amber-300 dark:from-amber-600 dark:to-amber-500 text-gray-900 dark:text-gray-900 px-10 py-4 rounded-full font-bold text-lg hover:shadow-lg transform hover:scale-105 transition-all duration-300"
              >
                Ver Todos los Productos
                <ArrowRight className="group-hover:translate-x-2 transition-transform duration-300" size={20} />
              </button>
            </div>
          </div>
        </div>

        <div
          className={`fixed inset-0 bg-black/40 z-50 flex justify-end transition-opacity duration-300 ${
            isFilterOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
          role="dialog"
          aria-modal={isFilterOpen}
        >
          <aside
            className={`w-80 h-full p-6 shadow-lg overflow-y-auto transform transition-transform duration-300 ease-in-out bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-200
              ${isFilterOpen ? 'translate-x-0' : 'translate-x-full'}`}
          >
            <button
              onClick={() => setIsFilterOpen(false)}
              className="absolute top-4 right-4 text-stone-700 dark:text-gray-300 hover:text-amber-500 dark:hover:text-amber-400 transition-colors"
              aria-label="Cerrar filtros"
            >
              <X size={28} />
            </button>

            <h3 className="text-lg font-bold mb-4">Filtros</h3>

            <div className="space-y-4">
              <div>
                <label className="block font-semibold mb-1">ColecciÃ³n</label>
                <select
                  value={collection}
                  onChange={(e) => { setCollection(e.target.value); setCurrentPage(1); }}
                  className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                >
                  {collections.map((col) => (
                    <option key={col} value={col}>
                      {col === 'all' ? 'TODAS' : col.replace('-', ' ').toUpperCase()}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block font-semibold mb-1">CategorÃ­a</label>
                <select
                  value={category}
                  onChange={(e) => { setCategory(e.target.value); setCurrentPage(1); }}
                  className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                >
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat === 'all' ? 'Todos' : cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block font-semibold mb-1">GÃ©nero</label>
                <select
                  value={gender}
                  onChange={(e) => { setGender(e.target.value); setCurrentPage(1); }}
                  className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                >
                  <option value="all">Todos</option>
                  <option value="men">Hombre</option>
                  <option value="women">Mujer</option>
                </select>
              </div>

              <div>
                <label className="block font-semibold mb-1">Ordenar por</label>
                <select
                  value={sort}
                  onChange={(e) => setSort(e.target.value)}
                  className="w-full px-3 py-2 border border-stone-300 dark:border-gray-600 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 dark:bg-gray-700 dark:text-gray-200"
                >
                  <option value="default">Por defecto</option>
                  <option value="price-asc">Precio: menor a mayor</option>
                  <option value="price-desc">Precio: mayor a menor</option>
                  <option value="name">Nombre</option>
                </select>
              </div>

              <div>
                <label className="block font-semibold mb-1">Rango de precio</label>
                <input
                  type="range"
                  min="0"
                  max="500"
                  step="5"
                  value={priceMax}
                  onChange={(e) => setPriceMax(Number(e.target.value))}
                  className="w-full accent-amber-500 cursor-pointer"
                />
                <p className="text-sm mt-1">Hasta ${priceMax}</p>
              </div>
            </div>
          </aside>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
