import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Sparkles,
  ArrowRight,
  ArrowLeft,
  Filter,
  Tag,
  Layers,
  Zap,
  TrendingUp,
  DollarSign,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';

const ProductList = () => {
  const { theme } = useTheme();
  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceMax, setPriceMax] = useState(200);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const containerRef = useRef(null);

  const productsPerPage = 6;
  const categories = useMemo(() => ['all', ...new Set(products.map((p) => p.category))], []);
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];

  const filteredProducts = useMemo(() => {
    const base = products
      .filter((p) => (category === 'all' ? true : p.category === category))
      .filter((p) => (gender === 'all' ? true : p.gender === gender))
      .filter((p) => p.price <= priceMax)
      .filter((p) => {
        switch (collection) {
          case 'novedades':
          case 'nuevos':
            return p.isNew === true;
          case 'mas-vendidos':
            return p.isBestseller === true;
          case 'ofertas':
            return p.isOnSale === true;
          default:
            return true;
        }
      });

    base.sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

    return base;
  }, [category, gender, collection, priceMax, sort]);

  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / productsPerPage));
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage, category, collection, gender, priceMax, sort]);

  useEffect(() => setCurrentPage(1), [category, collection, gender, priceMax, sort]);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  return (
    <section
      className="relative transition-colors duration-500 ease-in-out bg-gradient-to-b from-amber-100 via-white to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 py-20"
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Header */}
        <div className="text-center mb-10">
          <div className="inline-flex items-center gap-2 bg-amber-200 dark:bg-gray-800 px-6 py-2 rounded-full mb-4 shadow-sm transition-colors duration-500">
            <Sparkles size={18} className="text-amber-500 dark:text-amber-300" />
            <span className="text-sm font-bold text-stone-800 dark:text-gray-200">Colección Exclusiva</span>
          </div>

          <h2 className="text-4xl md:text-5xl font-extrabold text-stone-900 dark:text-white mb-2">
            Nuestra Colección
          </h2>
          <p className="text-md md:text-lg text-stone-600 dark:text-gray-300 max-w-3xl mx-auto leading-relaxed">
            Moda urbana con un toque elegante — seleccionada para quienes buscan estilo y comodidad.
          </p>
        </div>

        {/* Filtros rápidos */}
        <div className="flex items-center justify-between mb-6 gap-4">
          <div className="hidden md:flex items-center gap-3">
            <span className="text-sm text-stone-600 dark:text-gray-300">Filtros rápidos:</span>
            <div className="flex gap-2">
              <button
                onClick={() => { setCollection('novedades'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
              >
                Novedades
              </button>
              <button
                onClick={() => { setCollection('ofertas'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
              >
                Ofertas
              </button>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="hidden md:flex items-center gap-2 text-sm text-stone-600 dark:text-gray-300">
              <Tag size={16} /> <span>{filteredProducts.length} resultados</span>
            </div>

            <button
              onClick={() => setIsFilterOpen(true)}
              className="flex items-center gap-2 bg-amber-400 dark:bg-amber-600 text-gray-900 dark:text-gray-900 px-4 py-2 rounded-full font-semibold shadow hover:scale-105 transition-transform duration-300 md:hidden"
            >
              <Filter size={18} /> Filtros
            </button>
          </div>
        </div>

        <div className="flex gap-8 relative">
          {/* Sidebar */}
          <aside
            className={`hidden md:flex flex-col rounded-3xl p-6 shadow-[0_8px_30px_rgba(0,0,0,0.08)] space-y-6 max-h-[calc(100vh-220px)] overflow-y-auto sticky top-24 transition-all duration-500 backdrop-blur-md border border-white/20 dark:border-gray-700/40
              ${isSidebarOpen ? 'w-72 opacity-100 mr-6 bg-white/70 dark:bg-gray-800/70' : 'w-16 opacity-70 p-2 overflow-hidden'}`}
          >
            {/* Botón minimizar */}
            <div className="flex justify-end mb-2">
              <button
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                className="p-2 rounded-full bg-amber-400 dark:bg-amber-600 hover:scale-105 transition-transform"
              >
                {isSidebarOpen ? <ChevronLeft size={16} /> : <ChevronRight size={16} />}
              </button>
            </div>

            {isSidebarOpen && (
              <>
                {[{
                  icon: <Layers size={16} />,
                  label: 'Colección',
                  value: collection,
                  onChange: (e) => { setCollection(e.target.value); setCurrentPage(1); },
                  options: collections,
                  mapLabel: (v) => (v === 'all' ? 'TODAS' : v.replace('-', ' ').toUpperCase()),
                },{
                  icon: <Tag size={16} />,
                  label: 'Categoría',
                  value: category,
                  onChange: (e) => { setCategory(e.target.value); setCurrentPage(1); },
                  options: categories,
                  mapLabel: (v) => (v === 'all' ? 'Todos' : v.charAt(0).toUpperCase() + v.slice(1)),
                },{
                  icon: <Zap size={16} />,
                  label: 'Género',
                  value: gender,
                  onChange: (e) => { setGender(e.target.value); setCurrentPage(1); },
                  options: ['all','men','women'],
                  mapLabel: (v) => (v==='all'?'Todos':v==='men'?'Hombre':'Mujer'),
                },{
                  icon: <TrendingUp size={16} />,
                  label: 'Ordenar',
                  value: sort,
                  onChange: (e) => setSort(e.target.value),
                  options: ['default','price-asc','price-desc','name'],
                  mapLabel: (v)=> v==='default'?'Por defecto':v==='price-asc'?'Precio: menor a mayor':v==='price-desc'?'Precio: mayor a menor':'Nombre'
                }].map(({icon,label,value,onChange,options,mapLabel})=>(
                  <motion.div key={label} initial={{opacity:0,y:10}} animate={{opacity:1,y:0}} transition={{duration:0.4}} className="space-y-2">
                    <label className="flex items-center gap-2 text-sm font-semibold text-stone-700 dark:text-gray-300">{icon} {label}</label>
                    <select
                      value={value}
                      onChange={onChange}
                      className="w-full px-4 py-2 border border-stone-300/50 dark:border-gray-600/50 rounded-xl bg-white/60 dark:bg-gray-700/60 text-stone-800 dark:text-gray-200 focus:outline-none focus:ring-2 focus:ring-amber-400 dark:focus:ring-amber-500 transition-all duration-300 hover:bg-amber-50/40 dark:hover:bg-gray-700/80 shadow-sm"
                    >
                      {options.map(opt=><option key={opt} value={opt}>{mapLabel(opt)}</option>)}
                    </select>
                  </motion.div>
                ))}

                {/* Precio */}
                <motion.div initial={{opacity:0,y:10}} animate={{opacity:1,y:0}} transition={{duration:0.4}} className="space-y-3 pt-2">
                  <label className="flex items-center gap-2 text-sm font-semibold text-stone-700 dark:text-gray-300"><DollarSign size={16}/> Precio máximo</label>
                  <div className="flex items-center justify-between text-xs text-stone-500 dark:text-gray-400">
                    <span>$0</span>
                    <span>${priceMax}</span>
                  </div>
                  <input
                    type="range"
                    min="0"
                    max="500"
                    step="5"
                    value={priceMax}
                    onChange={e => { setPriceMax(Number(e.target.value)); setCurrentPage(1); }}
                    className="w-full accent-amber-500 cursor-pointer"
                  />
                </motion.div>

                {/* Limpiar filtros */}
                <motion.div initial={{opacity:0,y:10}} animate={{opacity:1,y:0}} transition={{duration:0.4}} className="mt-4">
                  <button onClick={()=>{
                    setCategory('all'); setCollection('all'); setGender('all'); setSort('default'); setPriceMax(200);
                  }} className="w-full py-2.5 rounded-xl font-semibold text-sm bg-gradient-to-r from-amber-400 to-amber-300 dark:from-amber-600 dark:to-amber-500 text-gray-900 shadow-md hover:scale-[1.02] active:scale-95 transition-transform duration-200">
                    Limpiar filtros
                  </button>
                </motion.div>
              </>
            )}
          </aside>

          {/* Grid de productos */}
          <div className="flex-1">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage}
                initial={{ opacity: 0, y: 12 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -12 }}
                transition={{ duration: 0.45 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product,index)=>(
                    <motion.div key={product.id} initial={{opacity:0,scale:0.98}} animate={{opacity:1,scale:1}} exit={{opacity:0,scale:0.98}} transition={{duration:0.35,delay:index*0.03}}>
                      <ProductCard product={product}/>
                    </motion.div>
                  ))
                ) : (
                  <p className="text-center text-stone-600 dark:text-gray-300 text-lg col-span-full">
                    No se encontraron productos que coincidan con los filtros.
                  </p>
                )}
              </motion.div>
            </AnimatePresence>

            {/* Paginación */}
            {totalPages>1 && (
              <div className="flex justify-center items-center gap-2 mb-8 flex-wrap">
                <button onClick={()=>handlePageChange(currentPage-1)} className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition" disabled={currentPage===1}><ArrowLeft size={18}/></button>
                {Array.from({length:totalPages},(_,i)=>i+1).map(page=>(
                  <button key={page} onClick={()=>handlePageChange(page)} className={`px-4 py-2 rounded-full border transition-all duration-200 ${currentPage===page?'bg-amber-400 text-gray-900 border-amber-400 dark:bg-amber-600 dark:text-gray-900 dark:border-amber-600':'bg-white text-stone-800 border-stone-300 hover:bg-amber-200 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600 dark:hover:bg-amber-500'}`}>{page}</button>
                ))}
                <button onClick={()=>handlePageChange(currentPage+1)} className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition" disabled={currentPage===totalPages}><ArrowRight size={18}/></button>
              </div>
            )}

            {/* CTA final */}
            <div className="text-center">
              <button onClick={()=>{
                setCategory('all'); setCollection('all'); setGender('all'); setSort('default'); setPriceMax(500); setCurrentPage(1);
              }} className="group inline-flex items-center gap-3 bg-gradient-to-r from-amber-400 to-amber-300 dark:from-amber-600 dark:to-amber-500 text-gray-900 dark:text-gray-900 px-10 py-4 rounded-full font-bold text-lg hover:shadow-lg transform hover:scale-105 transition-all duration-300">
                Ver Todos los Productos
                <ArrowRight className="group-hover:translate-x-2 transition-transform duration-300" size={20}/>
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
