import React, { useState, useEffect } from 'react';
import { ShoppingBag, Menu, X } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';

const Header = ({ setCurrentPage, currentPage }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  return (
    <header
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ${
        isScrolled
          ? 'bg-white/95 shadow-md backdrop-blur-sm'
          : 'bg-white'
      }`}
    >
      <div className="container mx-auto flex items-center justify-between px-6 py-4">
        
        {/* Logo */}
        <div
          onClick={() => setCurrentPage('home')}
          className="flex items-center gap-2 cursor-pointer"
        >
          <ShoppingBag className="w-7 h-7 text-gray-800" />
          <h1 className="text-xl font-semibold text-gray-800">Fashion Store</h1>
        </div>

        {/* Navegación */}
        <nav className="hidden md:flex items-center gap-8">
          {['home', 'productos', 'contacto'].map((page) => (
            <button
              key={page}
              onClick={() => setCurrentPage(page)}
              className={`capitalize text-sm font-medium transition-colors duration-300 ${
                currentPage === page
                  ? 'text-gray-900 border-b-2 border-gray-900'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              {page}
            </button>
          ))}
        </nav>

        {/* Botón menú móvil */}
        <button
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="md:hidden text-gray-800"
        >
          {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Menú móvil */}
      {isMenuOpen && (
        <div className="md:hidden bg-white border-t border-gray-200 shadow-sm">
          {['home', 'productos', 'contacto'].map((page) => (
            <button
              key={page}
              onClick={() => {
                setCurrentPage(page);
                setIsMenuOpen(false);
              }}
              className={`block w-full text-left px-6 py-3 text-gray-700 hover:bg-gray-100 ${
                currentPage === page ? 'font-semibold text-gray-900' : ''
              }`}
            >
              {page.charAt(0).toUpperCase() + page.slice(1)}
            </button>
          ))}
        </div>
      )}
    </header>
  );
};

export default Header;
