import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const Novedades = () => {
  const novedades = [
    {
      id: 1,
      title: 'Campera Urbana 2025',
      category: 'Hombre',
      price: '$89.900',
      description: 'Ligera, impermeable y con diseño moderno. Ideal para la ciudad.',
      image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-blue-100 text-blue-800',
    },
    {
      id: 2,
      title: 'Vestido Verano 2025',
      category: 'Mujer',
      price: '$62.500',
      description: 'Fresco, fluido y elegante. Perfecto para días soleados.',
      image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-pink-100 text-pink-800',
    },
    {
      id: 3,
      title: 'Remera Clásica Unisex',
      category: 'Unisex',
      price: '$24.900',
      description: 'Algodón premium, corte perfecto. Tu esencial diario.',
      image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-gray-100 text-gray-800',
    },
    {
      id: 4,
      title: 'Pantalón Cargo',
      category: 'Hombre',
      price: '$74.900',
      description: 'Resistente, funcional y con bolsillos amplios.',
      image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-green-100 text-green-800',
    },
  ];

  return (
    <section className="py-20" style={{ backgroundColor: '#fef3c7' }}>
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Encabezado */}
        <div className="text-center mb-14">
          <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-900 tracking-tight mb-4">
            Novedades
          </h2>
          <p className="text-lg text-gray-700 max-w-2xl mx-auto">
            Descubrí lo último en estilo, confort y tendencia para esta temporada.
          </p>
        </div>

        {/* Carrusel */}
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          spaceBetween={40}
          slidesPerView={1}
          navigation={{
            nextEl: '.novedades-next',
            prevEl: '.novedades-prev',
          }}
          pagination={{ clickable: true, el: '.novedades-pagination' }}
          autoplay={{ delay: 4500, disableOnInteraction: false }}
          loop={true}
          className="relative rounded-3xl shadow-2xl overflow-hidden"
        >
          {novedades.map((item) => (
            <SwiperSlide key={item.id}>
              <div className="grid grid-cols-1 md:grid-cols-2 h-full">
                {/* Imagen */}
                <div className="relative group">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                    loading="lazy"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent"></div>
                  <span
                    className={`absolute top-5 left-5 px-4 py-1.5 text-sm font-semibold rounded-full ${item.badgeColor} backdrop-blur-sm shadow-sm`}
                  >
                    {item.category}
                  </span>
                </div>

                {/* Texto */}
                <div className="flex flex-col justify-center px-8 py-10 bg-white/90 backdrop-blur-md">
                  <h3 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
                    {item.title}
                  </h3>
                  <p className="text-lg text-gray-700 leading-relaxed mb-6">
                    {item.description}
                  </p>
                  <div className="flex items-center justify-between mb-8">
                    <span className="text-3xl font-bold text-yellow-600">{item.price}</span>
                  </div>
                  <button className="px-8 py-3 bg-yellow-500 text-gray-900 font-bold rounded-full shadow-md hover:bg-yellow-600 hover:shadow-lg hover:scale-105 transition-transform duration-300">
                    Ver producto
                  </button>
                </div>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>

        {/* Navegación */}
        <div className="flex justify-center items-center mt-10 relative">
          <button className="novedades-prev absolute left-0 bg-white p-3 rounded-full shadow-md hover:bg-yellow-50 transition-all duration-300 -translate-y-1/2 top-1/2">
            <svg
              className="w-6 h-6 text-yellow-600"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <div className="novedades-pagination flex gap-3"></div>

          <button className="novedades-next absolute right-0 bg-white p-3 rounded-full shadow-md hover:bg-yellow-50 transition-all duration-300 -translate-y-1/2 top-1/2">
            <svg
              className="w-6 h-6 text-yellow-600"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      </div>

      {/* Estilo bullets */}
      <style jsx>{`
        .novedades-pagination .swiper-pagination-bullet {
          width: 12px;
          height: 12px;
          background-color: #fbbf24;
          opacity: 0.6;
          transition: all 0.3s ease;
        }
        .novedades-pagination .swiper-pagination-bullet-active {
          transform: scale(1.5);
          opacity: 1;
          background-color: #facc15;
        }
      `}</style>
    </section>
  );
};

export default Novedades;
