import React, { useState } from 'react';
import { Sparkles, ArrowRight } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import ProductCard from './ProductCard';
import { products } from '../data/products';

const ProductList = () => {
  const [ref, isVisible] = useIntersectionObserver();
  const scrollY = useScrollAnimation();

  // Estado para el filtro
  const [filter, setFilter] = useState('all');

  // Extraer categorías únicas de los productos
  const categories = ['all', ...new Set(products.map(p => p.category))];

  // Filtrar productos según el filtro activo
  const filteredProducts =
    filter === 'all' ? products : products.filter(p => p.category === filter);

  return (
    <section id="productos" className="py-20 bg-white relative overflow-hidden">
      <div className="container mx-auto px-4 relative z-10">
        {/* Título */}
        <div
          ref={ref}
          className={`text-center mb-16 ${isVisible ? 'animate-fade-in-up' : 'opacity-0'}`}
        >
          <div className="inline-flex items-center gap-2 bg-amber-100 px-6 py-2 rounded-full mb-4 shadow-sm">
            <Sparkles className="text-amber-600" size={20} />
            <span className="text-sm font-bold text-stone-800">
              Colección Exclusiva
            </span>
          </div>

          <h2 className="text-5xl md:text-6xl font-bold text-stone-900 mb-6">
            Nuestra Colección
          </h2>

          <p className="text-xl md:text-2xl text-stone-600 max-w-3xl mx-auto leading-relaxed">
            Descubre las últimas tendencias en moda. Cada pieza seleccionada con
            cuidado para ti.
          </p>
        </div>

        {/* Botones de filtro */}
        <div className="flex justify-center gap-4 mb-8 flex-wrap">
          {categories.map(cat => (
            <button
              key={cat}
              onClick={() => setFilter(cat)}
              className={`px-6 py-2 rounded-full font-semibold transition-all duration-300 ${
                filter === cat
                  ? 'bg-amber-500 text-white shadow-md'
                  : 'bg-white text-stone-700 border border-stone-300 hover:bg-amber-50 hover:text-amber-600'
              }`}
            >
              {cat.charAt(0).toUpperCase() + cat.slice(1)}
            </button>
          ))}
        </div>

        {/* Productos */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
          {filteredProducts.map((product, index) => (
            <ProductCard key={product.id} product={product} index={index} />
          ))}
        </div>

        {/* Botón final */}
        <div className="text-center">
          <button className="group bg-amber-500 text-white px-12 py-5 rounded-full font-bold text-lg hover:bg-amber-600 hover:shadow-lg transform hover:scale-105 transition-all duration-300 flex items-center gap-3 mx-auto">
            Ver Todos los Productos
            <ArrowRight
              className="group-hover:translate-x-2 transition-transform duration-300"
              size={24}
            />
          </button>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
