    hero: {
      title: 'Bienvenido a Mi Tienda',
      subtitle: 'Los mejores productos a tu alcance',
      button: 'Ver Productos',
    },
    // Añadir secciones como Novedades, ProductList, Contact, Footer...
  },
  en: {
    header: {
      home: 'Home',
      products: 'Products',
      contact: 'Contact',
      toggleLanguage: 'ES',
    },
    hero: {
      title: 'Welcome to My Store',
      subtitle: 'The best products at your fingertips',
      button: 'View Products',
    },
  },
};

