import React, { useState, useEffect, useContext } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { ThemeContext } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useContext(ThemeContext);

  // Cerrar menú al cambiar de página
  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home', action: scrollToProducts },
    { name: 'Contacto', page: 'contact' },
  ];

  return (
    <header className="fixed w-full z-50 bg-white dark:bg-neutral-950 shadow-md transition-colors duration-300">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
        {/* Logo */}
        <div className="text-2xl font-bold text-gray-900 dark:text-gray-100 cursor-pointer" onClick={() => setCurrentPage('home')}>
          Tienda
        </div>

        {/* Menú Desktop */}
        <nav className="hidden md:flex items-center gap-8">
          {menuItems.map((item) => (
            <button
              key={item.name}
              onClick={() => {
                if (item.action) item.action();
                else setCurrentPage(item.page);
              }}
              className={`font-semibold text-gray-900 dark:text-gray-100 hover:text-amber-500 transition-colors ${
                currentPage === item.page ? 'text-amber-400' : ''
              }`}
            >
              {item.name}
            </button>
          ))}
        </nav>

        {/* Acciones */}
        <div className="flex items-center gap-4">
          {/* Tema */}
          <button
            onClick={toggleTheme}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 hover:bg-amber-400 hover:text-gray-900 transition"
          >
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
          </button>

          {/* Menú Mobile */}
          <button className="md:hidden p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200" onClick={() => setIsMenuOpen(!isMenuOpen)}>
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Menú Mobile Drawer */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.nav
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            className="md:hidden bg-white dark:bg-neutral-950 overflow-hidden shadow-md"
          >
            <div className="flex flex-col gap-4 p-4">
              {menuItems.map((item) => (
                <button
                  key={item.name}
                  onClick={() => {
                    if (item.action) item.action();
                    else setCurrentPage(item.page);
                    setIsMenuOpen(false);
                  }}
                  className={`text-gray-900 dark:text-gray-100 font-semibold py-2 px-3 rounded hover:bg-amber-100 dark:hover:bg-amber-400 transition`}
                >
                  {item.name}
                </button>
              ))}
            </div>
          </motion.nav>
        )}
      </AnimatePresence>
    </header>
  );
};

export default Header;
