import React, { useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import { useTheme } from '../context/ThemeContext';
import { useLanguage } from '../context/LanguageContext';
import translations from '../i18n/translations';

const Novedades = () => {
  const swiperRef = useRef(null);
  const { theme } = useTheme();
  const { language } = useLanguage();
  const t = translations[language]?.novedades || translations['es'].novedades;

  return (
    <section
      className={`py-12 sm:py-16 transition-colors duration-500 ${
        theme === 'dark' ? 'bg-neutral-900' : 'bg-yellow-100'
      }`}
    >
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Encabezado */}
        <div className="text-center mb-8 sm:mb-12">
          <h2
            className={`text-3xl sm:text-5xl font-extrabold mb-2 sm:mb-3 ${
              theme === 'dark' ? 'text-white' : 'text-gray-900'
            }`}
          >
            {t.title}
          </h2>
          <p
            className={`text-base sm:text-lg max-w-2xl mx-auto ${
              theme === 'dark' ? 'text-gray-300' : 'text-gray-700'
            }`}
          >
            {t.description}
          </p>
        </div>

        {/* Carrusel */}
        <div
          onMouseEnter={() => swiperRef.current?.autoplay?.stop()}
          onMouseLeave={() => swiperRef.current?.autoplay?.start()}
        >
          <Swiper
            modules={[Pagination, Autoplay]}
            spaceBetween={20}
            slidesPerView={1}
            pagination={{ clickable: true, el: '.novedades-pagination' }}
            autoplay={{
              delay: 4000,
              disableOnInteraction: false,
            }}
            loop={true}
            onSwiper={(swiper) => (swiperRef.current = swiper)}
            className="relative rounded-2xl sm:rounded-3xl shadow-xl overflow-hidden"
          >
            {t.items.map((item) => (
              <SwiperSlide key={item.id}>
                <div
                  className={`flex flex-col md:flex-row items-center justify-center rounded-2xl overflow-hidden shadow-lg transition-all duration-500 ${
                    theme === 'dark' ? 'bg-neutral-800/70' : 'bg-white/90'
                  }`}
                >
                  {/* Imagen */}
                  <div className="w-full md:w-1/2 flex justify-center items-center bg-gray-100 p-5 sm:p-6 relative">
                    <img
                      src={item.image}
                      alt={item.title}
                      className="w-auto h-[280px] sm:h-[340px] object-contain transition-transform duration-700 hover:scale-105"
                      loading="lazy"
                    />
                    <span
                      className={`absolute top-4 left-4 px-3 py-1 text-xs sm:text-sm font-semibold rounded-full ${item.badgeColor} backdrop-blur-sm shadow-sm`}
                    >
                      {item.category}
                    </span>
                  </div>

                  {/* Texto */}
                  <div className="w-full md:w-1/2 flex flex-col justify-center text-center md:text-left p-5 sm:p-7 lg:p-8">
                    <h3
                      className={`text-xl sm:text-2xl md:text-3xl font-bold mb-2 ${
                        theme === 'dark' ? 'text-white' : 'text-gray-900'
                      }`}
                    >
                      {item.title}
                    </h3>
                    <p
                      className={`text-sm sm:text-base leading-relaxed mb-3 ${
                        theme === 'dark' ? 'text-gray-300' : 'text-gray-700'
                      }`}
                    >
                      {item.description}
                    </p>
                    <span
                      className={`text-2xl font-bold mb-4 block ${
                        theme === 'dark' ? 'text-amber-400' : 'text-yellow-600'
                      }`}
                    >
                      {item.price}
                    </span>
                    <div className="flex justify-center md:justify-start">
                      <button
                        className={`px-4 py-2 font-semibold rounded-full shadow-md hover:scale-105 transition-all duration-300 text-sm sm:text-base ${
                          theme === 'dark'
                            ? 'bg-amber-500 text-gray-900 hover:bg-amber-600'
                            : 'bg-yellow-500 text-gray-900 hover:bg-yellow-600'
                        }`}
                      >
                        {t.button}
                      </button>
                    </div>
                  </div>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>

          {/* Paginación */}
          <div className="novedades-pagination flex justify-center mt-6 sm:mt-7 gap-3"></div>
        </div>
      </div>

      {/* Estilo bullets */}
      <style jsx>{`
        .novedades-pagination .swiper-pagination-bullet {
          width: 10px;
          height: 10px;
          background-color: #fbbf24;
          opacity: 0.6;
          transition: all 0.3s ease;
        }
        .novedades-pagination .swiper-pagination-bullet-active {
          transform: scale(1.4);
          opacity: 1;
          background-color: #facc15;
        }
      `}</style>
    </section>
  );
};

export default Novedades;
