import React, { useState } from 'react';
import { Sparkles, ArrowRight, Filter } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import ProductCard from './ProductCard';
import { products } from '../data/products';

const ProductList = () => {
  const [ref, isVisible] = useIntersectionObserver();
  const scrollY = useScrollAnimation();

  // Estados para filtros
  const [category, setCategory] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 200]);

  // Categorías únicas
  const categories = ['all', ...new Set(products.map(p => p.category))];

  // Filtrado avanzado
  const filteredProducts = products
    .filter(p => (category === 'all' ? true : p.category === category))
    .filter(p => (gender === 'all' ? true : p.gender === gender))
    .filter(p => p.price >= priceRange[0] && p.price <= priceRange[1])
    .sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

  return (
    <section id="productos" className="py-20 bg-white relative overflow-hidden">
      <div className="container mx-auto px-4 relative z-10">
        {/* Título */}
        <div
          ref={ref}
          className={`text-center mb-16 ${isVisible ? 'animate-fade-in-up' : 'opacity-0'}`}
        >
          <div className="inline-flex items-center gap-2 bg-amber-100 px-6 py-2 rounded-full mb-4 shadow-sm">
            <Sparkles className="text-amber-600" size={20} />
            <span className="text-sm font-bold text-stone-800">
              Colección Exclusiva
            </span>
          </div>

          <h2 className="text-5xl md:text-6xl font-bold text-stone-900 mb-6">
            Nuestra Colección
          </h2>

          <p className="text-xl md:text-2xl text-stone-600 max-w-3xl mx-auto leading-relaxed">
            Moda masculina y femenina con estilo. Encuentra tu look ideal entre nuestras selecciones exclusivas.
          </p>
        </div>

        {/* Controles de filtro */}
        <div className="bg-stone-50 border border-stone-200 rounded-2xl p-6 mb-12 shadow-sm">
          <div className="flex flex-wrap justify-center gap-6 items-center">
            {/* Filtro por categoría */}
            <div>
              <label className="block text-stone-700 font-semibold mb-2 text-sm">
                Categoría
              </label>
              <select
                value={category}
                onChange={e => setCategory(e.target.value)}
                className="px-4 py-2 border border-stone-300 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 text-stone-700 bg-white"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat.charAt(0).toUpperCase() + cat.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            {/* Filtro por género */}
            <div>
              <label className="block text-stone-700 font-semibold mb-2 text-sm">
                Género
              </label>
              <select
                value={gender}
                onChange={e => setGender(e.target.value)}
                className="px-4 py-2 border border-stone-300 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 text-stone-700 bg-white"
              >
                <option value="all">Todos</option>
                <option value="men">Hombre</option>
                <option value="women">Mujer</option>
              </select>
            </div>

            {/* Ordenar */}
            <div>
              <label className="block text-stone-700 font-semibold mb-2 text-sm">
                Ordenar por
              </label>
              <select
                value={sort}
                onChange={e => setSort(e.target.value)}
                className="px-4 py-2 border border-stone-300 rounded-full focus:outline-none focus:ring-2 focus:ring-amber-400 text-stone-700 bg-white"
              >
                <option value="default">Por defecto</option>
                <option value="price-asc">Precio: menor a mayor</option>
                <option value="price-desc">Precio: mayor a menor</option>
                <option value="name">Nombre</option>
              </select>
            </div>

            {/* Rango de precio */}
            <div>
              <label className="block text-stone-700 font-semibold mb-2 text-sm">
                Rango de precio
              </label>
              <input
                type="range"
                min="0"
                max="200"
                step="10"
                value={priceRange[1]}
                onChange={e => setPriceRange([0, Number(e.target.value)])}
                className="w-48 accent-amber-500"
              />
              <p className="text-stone-600 text-sm mt-1">
                Hasta ${priceRange[1].toFixed(2)}
              </p>
            </div>
          </div>
        </div>

        {/* Productos */}
        {filteredProducts.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
            {filteredProducts.map((product, index) => (
              <ProductCard key={product.id} product={product} index={index} />
            ))}
          </div>
        ) : (
          <p className="text-center text-stone-600 text-lg mb-12">
            No se encontraron productos que coincidan con los filtros seleccionados.
          </p>
        )}

        {/* Botón final */}
        <div className="text-center">
          <button className="group bg-amber-500 text-white px-12 py-5 rounded-full font-bold text-lg hover:bg-amber-600 hover:shadow-lg transform hover:scale-105 transition-all duration-300 flex items-center gap-3 mx-auto">
            Ver Todos los Productos
            <ArrowRight
              className="group-hover:translate-x-2 transition-transform duration-300"
              size={24}
            />
          </button>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
