import React, { useState, useEffect, useCallback } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';

// Simulación del contexto de tema (ajusta según tu implementación real)
const useTheme = () => {
  const [theme, setTheme] = useState('light');
  const toggleTheme = () => setTheme(prev => prev === 'light' ? 'dark' : 'light');
  
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);
  
  return { theme, toggleTheme };
};

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  // Cerrar menú al cambiar de página
  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  // Prevenir scroll del body cuando el menú está abierto
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isMenuOpen]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') {
          scrollToProducts();
        } else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
      } else {
        setCurrentPage(item.page);
        if (item.page === 'home') {
          window.scrollTo({ top: 0, behavior: 'smooth' });
        }
      }
      setIsMenuOpen(false);
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  return (
    <>
      <header className="fixed w-full z-50 backdrop-blur-md bg-white/90 dark:bg-gray-900/90 shadow-md transition-colors duration-300">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
          {/* Logo */}
          <div
            className="text-2xl font-extrabold text-gray-900 dark:text-gray-100 cursor-pointer hover:text-amber-500 transition-colors duration-300 active:scale-95"
            onClick={() => {
              setCurrentPage('home');
              window.scrollTo({ top: 0, behavior: 'smooth' });
              setIsMenuOpen(false);
            }}
          >
            Tienda
          </div>

          {/* Navegación desktop */}
          <nav className="hidden md:flex items-center gap-8">
            {menuItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleMenuClick(item)}
                className={`font-semibold text-gray-900 dark:text-gray-200 transition-all duration-300 focus:outline-none hover:scale-110 active:scale-95 ${
                  currentPage === item.page ? 'text-amber-500' : 'hover:text-amber-500'
                }`}
              >
                {item.name}
              </button>
            ))}
          </nav>

          {/* Botones: modo oscuro y menú móvil */}
          <div className="flex items-center gap-4 z-50">
            {/* Toggle tema */}
            <button
              onClick={toggleTheme}
              className="p-2.5 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
              hover:bg-amber-400 dark:hover:bg-amber-500 hover:text-gray-900 hover:scale-110
              transition-all duration-300 ease-in-out shadow-sm active:scale-95 focus:outline-none"
              aria-label="Cambiar tema"
            >
              {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
            </button>

            {/* Botón menú móvil mejorado */}
            <button
              className="md:hidden relative w-10 h-10 flex flex-col justify-center items-center p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 shadow-sm cursor-pointer transition-all duration-300 hover:bg-amber-400 dark:hover:bg-amber-500 hover:scale-110 active:scale-95 focus:outline-none"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              aria-label="Menú"
              aria-expanded={isMenuOpen}
            >
              <span
                className={`block w-5 h-0.5 bg-current rounded transition-all duration-300 ${
                  isMenuOpen ? 'rotate-45 translate-y-1.5' : ''
                }`}
              />
              <span
                className={`block w-5 h-0.5 bg-current rounded transition-all duration-300 my-1 ${
                  isMenuOpen ? 'opacity-0' : 'opacity-100'
                }`}
              />
              <span
                className={`block w-5 h-0.5 bg-current rounded transition-all duration-300 ${
                  isMenuOpen ? '-rotate-45 -translate-y-1.5' : ''
                }`}
              />
            </button>
          </div>
        </div>
      </header>

      {/* Overlay y menú móvil */}
      <div
        className={`fixed inset-0 bg-black/60 backdrop-blur-sm z-40 transition-opacity duration-300 ${
          isMenuOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
        }`}
        onClick={() => setIsMenuOpen(false)}
        aria-hidden="true"
      />

      {/* Menú lateral deslizante */}
      <nav
        className={`fixed top-0 right-0 h-full w-80 max-w-[85vw] bg-white dark:bg-gray-900 z-50 shadow-2xl p-6 flex flex-col transition-transform duration-300 ease-out ${
          isMenuOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        {/* Header del menú limpio */}
        <div className="flex justify-end items-center mb-8">
          <button
            onClick={() => setIsMenuOpen(false)}
            className="p-2 rounded-full bg-gray-100 dark:bg-gray-800 hover:bg-red-500 dark:hover:bg-red-500 text-gray-700 dark:text-gray-200 hover:text-white transition-all duration-300 hover:rotate-90 focus:outline-none"
            aria-label="Cerrar menú"
          >
            <X size={20} />
          </button>
        </div>

        {/* Items del menú centrados */}
        <div className="flex flex-col gap-6 mt-8 items-center">
          {menuItems.map((item, index) => (
            <button
              key={item.name}
              onClick={() => handleMenuClick(item)}
              className={`group relative font-bold py-4 px-8 rounded-xl bg-transparent
                transition-all duration-300 text-center overflow-hidden min-w-[200px]
                hover:scale-110 active:scale-95 focus:outline-none
                ${
                  currentPage === item.page
                    ? 'text-amber-500 dark:text-amber-400'
                    : 'text-gray-700 dark:text-gray-300 hover:text-amber-500 dark:hover:text-amber-400'
                }
              `}
              style={{
                animationDelay: `${index * 50}ms`,
                animation: isMenuOpen ? 'slideInRight 0.3s ease-out forwards' : 'none'
              }}
            >
              {/* Efecto de brillo al hover */}
              <span className="absolute inset-0 bg-gradient-to-r from-transparent via-amber-500/10 to-transparent translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700" />
              
              {/* Subrayado animado para página activa */}
              {currentPage === item.page && (
                <span className="absolute bottom-2 left-1/2 -translate-x-1/2 w-12 h-1 bg-gradient-to-r from-amber-500 to-orange-500 dark:from-amber-400 dark:to-orange-400 rounded-full" />
              )}
              
              <span className="relative z-10 text-lg">{item.name}</span>
            </button>
          ))}
        </div>

        {/* Separador decorativo */}
        <div className="flex-1" />

        {/* Footer del menú minimalista */}
        <div className="mt-auto pt-6">
          <p className="text-sm font-medium text-gray-500 dark:text-gray-400 text-center">
            © 2024 Tienda
          </p>
        </div>
      </nav>

      <style jsx>{`
        @keyframes slideInRight {
          from {
            opacity: 0;
            transform: translateX(20px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
      `}</style>
    </>
  );
};

// Componente de demostración
export default function App() {
  const [currentPage, setCurrentPage] = useState('home');

  const scrollToProducts = () => {
    console.log('Scrolling to products...');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-amber-50 to-orange-50 dark:from-gray-950 dark:via-gray-900 dark:to-gray-950 transition-colors duration-300">
      <Header 
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        scrollToProducts={scrollToProducts}
      />
      <main className="pt-24 px-4">
        <div className="container mx-auto">
