import React from 'react';
import { Sparkles, ArrowRight } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import ProductCard from './ProductCard';
import { products } from '../data/products';

const ProductList = () => {
  const [ref, isVisible] = useIntersectionObserver();
  const scrollY = useScrollAnimation();

  return (
    <section id="productos" className="py-20 bg-gradient-to-b from-gray-50 to-white relative overflow-hidden">
      <div className="absolute top-0 left-0 w-96 h-96 bg-purple-200/30 rounded-full blur-3xl" style={{ transform: `translateY(${scrollY * 0.1}px)` }}></div>
      <div className="absolute bottom-0 right-0 w-96 h-96 bg-pink-200/30 rounded-full blur-3xl" style={{ transform: `translateY(-${scrollY * 0.1}px)` }}></div>
      
      <div className="container mx-auto px-4 relative z-10">
        <div ref={ref} className={`text-center mb-16 ${isVisible ? 'animate-fade-in-up' : 'opacity-0'}`}>
          <div className="inline-flex items-center gap-2 bg-gradient-to-r from-purple-100 to-pink-100 px-6 py-2 rounded-full mb-4">
            <Sparkles className="text-purple-600" size={20} />
            <span className="text-sm font-bold text-purple-600">Colección Exclusiva</span>
          </div>
          
          <h2 className="text-5xl md:text-6xl font-bold bg-gradient-to-r from-purple-600 via-pink-500 to-orange-400 bg-clip-text text-transparent mb-6">
            Nuestra Colección
          </h2>
          <p className="text-xl md:text-2xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
            Descubre las últimas tendencias en moda. Cada pieza seleccionada con cuidado para ti.
          </p>
        </div>
        
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
          {products.map((product, index) => (
            <ProductCard key={product.id} product={product} index={index} />
          ))}
        </div>
        
        <div className="text-center">
          <button className="group bg-gradient-to-r from-purple-600 via-pink-500 to-orange-400 text-white px-12 py-5 rounded-full font-bold text-lg hover:shadow-2xl hover:shadow-purple-500/50 transform hover:scale-110 transition-all duration-300 flex items-center gap-3 mx-auto">
            Ver Todos los Productos
            <ArrowRight className="group-hover:translate-x-2 transition-transform duration-300" size={24} />
          </button>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
