import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay, EffectFade } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/effect-fade';

const Novedades = () => {
  const novedades = [
    {
      id: 1,
      title: 'Campera Urbana 2025',
      category: 'Hombre',
      price: '$89.900',
      description: 'Ligera, impermeable y con diseño moderno. Ideal para la ciudad.',
      image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-blue-100 text-blue-800',
    },
    {
      id: 2,
      title: 'Vestido Verano 2025',
      category: 'Mujer',
      price: '$62.500',
      description: 'Fresco, fluido y elegante. Perfecto para días soleados.',
      image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-pink-100 text-pink-800',
    },
    {
      id: 3,
      title: 'Remera Clásica Unisex',
      category: 'Unisex',
      price: '$24.900',
      description: 'Algodón premium, corte perfecto. Tu esencial diario.',
      image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-gray-100 text-gray-800',
    },
    {
      id: 4,
      title: 'Pantalón Cargo',
      category: 'Hombre',
      price: '$74.900',
      description: 'Resistente, funcional y con bolsillos amplios.',
      image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=1200&q=80',
      badgeColor: 'bg-green-100 text-green-800',
    },
  ];

  return (
    <section className="py-16 bg-gradient-to-br from-yellow-100 via-yellow-50 to-yellow-100">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Título */}
        <div className="text-center mb-10">
          <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-900 mb-3">
            Novedades
          </h2>
          <p className="text-lg text-gray-600">Descubre lo último en tendencias</p>
        </div>

        {/* Swiper Carrusel */}
        <Swiper
          modules={[Navigation, Pagination, Autoplay, EffectFade]}
          spaceBetween={30}
          slidesPerView={1}
          effect="fade"
          fadeEffect={{ crossFade: true }}
          navigation={{
            nextEl: '.novedades-next',
            prevEl: '.novedades-prev',
          }}
          pagination={{ clickable: true, el: '.novedades-pagination' }}
          autoplay={{ delay: 4000, disableOnInteraction: false }}
          loop={true}
          className="novedades-hero-swiper rounded-3xl overflow-hidden shadow-2xl"
        >
          {novedades.map((item) => (
            <SwiperSlide key={item.id}>
              <div className="grid grid-cols-1 md:grid-cols-2 h-full min-h-[24rem]">
                {/* Imagen */}
                <div className="relative overflow-hidden bg-gray-100">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="w-full h-full object-cover transition-transform duration-1000 hover:scale-105"
                    loading="lazy"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent opacity-50"></div>
                  <span
                    className={`absolute bottom-4 left-4 px-4 py-1.5 text-sm font-bold rounded-full ${item.badgeColor} shadow-md`}
                  >
                    {item.category}
                  </span>
                </div>

                {/* Contenido */}
                <div className="flex flex-col justify-center p-8 md:p-12 bg-white/80 backdrop-blur-sm">
                  <h3 className="text-3xl md:text-4xl font-extrabold text-gray-900 mb-3">
                    {item.title}
                  </h3>
                  <p className="text-lg text-gray-700 mb-5 leading-relaxed">
                    {item.description}
                  </p>
                  <div className="flex items-center justify-between mb-6">
                    <span className="text-3xl font-bold text-yellow-600">{item.price}</span>
                  </div>
                  <button className="w-full md:w-auto px-8 py-3 bg-yellow-500 text-gray-900 font-bold rounded-full shadow-lg hover:bg-yellow-600 transition-all duration-300 hover:shadow-xl hover:scale-105 focus:outline-none focus:ring-4 focus:ring-yellow-300">
                    Ver producto
                  </button>
                </div>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>

        {/* Controles personalizados */}
        <div className="flex justify-center items-center gap-4 mt-8 relative">
          <button className="novedades-prev bg-white p-3 rounded-full shadow-lg hover:bg-yellow-50 hover:shadow-xl transition-all absolute left-0 top-1/2 -translate-y-1/2 z-10">
            <svg className="w-6 h-6 text-yellow-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <div className="novedades-pagination flex gap-2"></div>

          <button className="novedades-next bg-white p-3 rounded-full shadow-lg hover:bg-yellow-50 hover:shadow-xl transition-all absolute right-0 top-1/2 -translate-y-1/2 z-10">
            <svg className="w-6 h-6 text-yellow-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      </div>

      {/* Estilo bullets personalizados */}
      <style jsx>{`
        .novedades-pagination .swiper-pagination-bullet {
          width: 12px;
          height: 12px;
          background-color: #fbbf24; /* amarillo similar al Hero */
          opacity: 1;
        }
        .novedades-pagination .swiper-pagination-bullet-active {
          transform: scale(1.5);
        }
      `}</style>
    </section>
  );
};

export default Novedades;
