import React, { useState, useEffect, useCallback } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') {
          scrollToProducts();
        } else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
      } else {
        setCurrentPage(item.page);
      }
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  return (
    <header className="fixed w-full z-50 backdrop-blur-md bg-white/70 dark:bg-gray-900/80 shadow-md transition-colors duration-500 ease-in-out">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">

        {/* Logo */}
        <div
          className="text-2xl font-extrabold text-gray-900 dark:text-gray-100 cursor-pointer hover:text-amber-500 transition-colors duration-500"
          onClick={() => setCurrentPage('home')}
        >
          Tienda
        </div>

        {/* Navegaci√≥n desktop */}
        <nav className="hidden md:flex items-center gap-8">
          {menuItems.map((item) => (
            <button
              key={item.name}
              onClick={() => handleMenuClick(item)}
              className={`font-semibold text-gray-900 dark:text-gray-200 relative transition-colors duration-500 ease-in-out
                ${currentPage === item.page
                  ? 'text-amber-400 after:absolute after:-bottom-1 after:left-0 after:w-full after:h-1 after:bg-amber-400 after:rounded-full'
                  : 'hover:text-amber-400'}
              `}
            >
              {item.name}
            </button>
          ))}
        </nav>

        {/* Botones: modo oscuro y men√∫ m√≥vil */}
        <div className="flex items-center gap-4 relative z-50">

          {/* Toggle tema con animaci√≥n */}
          <motion.div
            whileTap={{ scale: 0.9, rotate: 20 }}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
            hover:bg-gradient-to-r hover:from-amber-400 hover:to-yellow-300 dark:hover:from-amber-500 dark:hover:to-amber-400 
            shadow-lg cursor-pointer select-none transition-all duration-500"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              toggleTheme();
            }}
          >
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
          </motion.div>

          {/* Bot√≥n men√∫ m√≥vil */}
          <button
            className="md:hidden p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
            hover:bg-amber-400 dark:hover:bg-amber-500 shadow-lg transition-all duration-500"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X size={26} /> : <Menu size={26} />}
          </button>
        </div>
      </div>

      {/* Navegaci√≥n m√≥vil animada */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.nav
            initial={{ x: '100%', opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: '100%', opacity: 0 }}
            transition={{ duration: 0.4, ease: 'easeInOut' }}
            className="md:hidden fixed top-0 right-0 w-3/4 h-full bg-gradient-to-b from-yellow-50 via-amber-100 to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-950 backdrop-blur-xl shadow-2xl overflow-hidden z-40"
          >
            <div className="flex flex-col gap-6 p-6 mt-20">
              {menuItems.map((item) => (
                <motion.button
                  key={item.name}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => {
                    handleMenuClick(item);
                    setIsMenuOpen(false);
                  }}
                  className="text-gray-900 dark:text-gray-200 font-semibold py-3 px-4 rounded-xl 
                  hover:bg-amber-200 dark:hover:bg-amber-500 transition-colors duration-500 ease-in-out shadow-sm"
                >
                  {item.name}
                </motion.button>
              ))}
            </div>
          </motion.nav>
        )}
      </AnimatePresence>
    </header>
  );
};

export default Header;
