import React, { useState, useRef, useEffect, useMemo } from 'react';
import {
  Sparkles,
  ArrowRight,
  ArrowLeft,
  Filter,
  X,
  ChevronLeft,
  ChevronRight,
  Tag,
  Layers,
  Zap,
  TrendingUp,
  DollarSign,
} from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import ProductCard from './ProductCard';
import { products } from '../data/products';
import { useTheme } from '../context/ThemeContext';

const ProductList = () => {
  // tema para forzar re-render y sincronizar transiciones
  const { theme } = useTheme();

  // filtros y estado UI
  const [category, setCategory] = useState('all');
  const [collection, setCollection] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceMax, setPriceMax] = useState(200);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const containerRef = useRef(null);

  const productsPerPage = 6;
  const categories = useMemo(() => ['all', ...new Set(products.map((p) => p.category))], []);
  const collections = ['all', 'novedades', 'nuevos', 'mas-vendidos', 'ofertas'];

  // Filtrado + ordenado memoizado para performance
  const filteredProducts = useMemo(() => {
    const base = products
      .filter((p) => (category === 'all' ? true : p.category === category))
      .filter((p) => (gender === 'all' ? true : p.gender === gender))
      .filter((p) => p.price <= priceMax)
      .filter((p) => {
        switch (collection) {
          case 'novedades':
          case 'nuevos':
            return p.isNew === true;
          case 'mas-vendidos':
            return p.isBestseller === true;
          case 'ofertas':
            return p.isOnSale === true;
          default:
            return true;
        }
      });

    base.sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

    return base;
  }, [category, gender, collection, priceMax, sort]);

  const totalPages = Math.max(1, Math.ceil(filteredProducts.length / productsPerPage));
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  // scroll to top of products on page change
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [currentPage, theme]); // también re-render on theme para sincronía visual

  // manejo de paginación
  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);
  };

  // Reset paginación cuando cambian filtros
  useEffect(() => setCurrentPage(1), [category, collection, gender, priceMax, sort]);

  return (
    <section
      key={theme} // forzar re-mount/actualización visual cuando cambia tema
      className="relative transition-colors duration-500 ease-in-out bg-gradient-to-b from-amber-100 via-white to-white dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 py-20"
    >
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Header */}
        <div className="text-center mb-10">
          <div className="inline-flex items-center gap-2 bg-amber-200 dark:bg-gray-800 px-6 py-2 rounded-full mb-4 shadow-sm transition-colors duration-500">
            <Sparkles size={18} className="text-amber-500 dark:text-amber-300" />
            <span className="text-sm font-bold text-stone-800 dark:text-gray-200">Colección Exclusiva</span>
          </div>

          <h2 className="text-4xl md:text-5xl font-extrabold text-stone-900 dark:text-white mb-2">Nuestra Colección</h2>
          <p className="text-md md:text-lg text-stone-600 dark:text-gray-300 max-w-3xl mx-auto leading-relaxed">
            Moda urbana con un toque elegante — seleccionada para quienes buscan estilo y comodidad.
          </p>
        </div>

        {/* Mobile filter button */}
        <div className="flex items-center justify-between mb-6 gap-4">
          <div className="hidden md:flex items-center gap-3">
            <span className="text-sm text-stone-600 dark:text-gray-300">Filtros rápidos:</span>
            <div className="flex gap-2">
              <button
                onClick={() => { setCollection('novedades'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
                title="Novedades"
              >
                Novedades
              </button>
              <button
                onClick={() => { setCollection('ofertas'); setCurrentPage(1); }}
                className="px-3 py-1 rounded-full text-sm font-semibold bg-white dark:bg-gray-700 shadow-sm hover:opacity-90 transition duration-200"
                title="Ofertas"
              >
                Ofertas
              </button>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="hidden md:flex items-center gap-2 text-sm text-stone-600 dark:text-gray-300">
              <Tag size={16} /> <span>{filteredProducts.length} resultados</span>
            </div>

            <button
              onClick={() => setIsFilterOpen(true)}
              className="flex items-center gap-2 bg-amber-400 dark:bg-amber-600 text-gray-900 dark:text-gray-900 px-4 py-2 rounded-full font-semibold shadow hover:scale-105 transition-transform duration-300 md:hidden"
              aria-label="Abrir filtros"
            >
              <Filter size={18} /> Filtros
            </button>
          </div>
        </div>

        <div className="flex gap-8 relative">
          {/* Toggle sidebar */}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="hidden md:flex absolute -left-5 top-2 z-10 items-center justify-center bg-amber-400 dark:bg-amber-600 text-gray-900 dark:text-gray-900 p-2 rounded-full shadow hover:scale-105 transition-transform duration-300"
            aria-label="Mostrar/ocultar filtros"
          >
            {isSidebarOpen ? <ChevronLeft size={18} /> : <ChevronRight size={18} />}
          </button>

          {/* Sidebar Desktop */}
          <aside
            className={`hidden md:flex flex-col rounded-2xl shadow-xl overflow-hidden max-h-[calc(100vh-220px)] sticky top-24 transition-all duration-500 bg-gradient-to-br from-white via-amber-50/30 to-white dark:from-gray-800 dark:via-gray-800/50 dark:to-gray-900 border border-amber-200/50 dark:border-gray-700
              ${isSidebarOpen ? 'w-80 opacity-100 mr-4' : 'w-0 opacity-0 p-0 mr-0 overflow-hidden'}`}
            aria-hidden={!isSidebarOpen}
          >
            {isSidebarOpen && (
              <>
                {/* Header con gradiente */}
                <div className="bg-gradient-to-r from-amber-400 to-amber-500 dark:from-amber-600 dark:to-amber-700 px-6 py-5">
                  <div className="flex items-center gap-3">
                    <div className="p-2 bg-white/20 rounded-lg backdrop-blur-sm">
                      <Filter size={20} className="text-white" />
                    </div>
                    <div>
                      <h3 className="text-lg font-bold text-white">Filtros</h3>
                      <p className="text-xs text-white/80">{filteredProducts.length} productos</p>
                    </div>
                  </div>
                </div>

                {/* Contenido scrolleable */}
                <div className="overflow-y-auto flex-1 px-6 py-5 space-y-5">
                  {/* Collections */}
                  <div className="space-y-3">
                    <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                      <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                        <Layers size={14} className="text-amber-600 dark:text-amber-400" />
                      </div>
                      Colección
                    </label>
                    <select
                      value={collection}
                      onChange={(e) => { setCollection(e.target.value); setCurrentPage(1); }}
                      className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm hover:shadow-md"
                    >
                      {collections.map((col) => (
                        <option key={col} value={col}>
                          {col === 'all' ? 'Todas las colecciones' : col.replace('-', ' ').toUpperCase()}
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* Divider */}
                  <div className="border-t border-stone-200 dark:border-gray-700"></div>

                  {/* Category */}
                  <div className="space-y-3">
                    <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                      <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                        <Tag size={14} className="text-amber-600 dark:text-amber-400" />
                      </div>
                      Categoría
                    </label>
                    <select
                      value={category}
                      onChange={(e) => { setCategory(e.target.value); setCurrentPage(1); }}
                      className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm hover:shadow-md"
                    >
                      {categories.map((cat) => (
                        <option key={cat} value={cat}>
                          {cat === 'all' ? 'Todas las categorías' : cat.charAt(0).toUpperCase() + cat.slice(1)}
                        </option>
                      ))}
                    </select>
                  </div>

                  {/* Divider */}
                  <div className="border-t border-stone-200 dark:border-gray-700"></div>

                  {/* Gender */}
                  <div className="space-y-3">
                    <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                      <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                        <Zap size={14} className="text-amber-600 dark:text-amber-400" />
                      </div>
                      Género
                    </label>
                    <div className="grid grid-cols-3 gap-2">
                      <button
                        onClick={() => { setGender('all'); setCurrentPage(1); }}
                        className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                          gender === 'all'
                            ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                            : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                        }`}
                      >
                        Todos
                      </button>
                      <button
                        onClick={() => { setGender('men'); setCurrentPage(1); }}
                        className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                          gender === 'men'
                            ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                            : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                        }`}
                      >
                        Hombre
                      </button>
                      <button
                        onClick={() => { setGender('women'); setCurrentPage(1); }}
                        className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                          gender === 'women'
                            ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                            : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                        }`}
                      >
                        Mujer
                      </button>
                    </div>
                  </div>

                  {/* Divider */}
                  <div className="border-t border-stone-200 dark:border-gray-700"></div>

                  {/* Sort */}
                  <div className="space-y-3">
                    <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                      <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                        <TrendingUp size={14} className="text-amber-600 dark:text-amber-400" />
                      </div>
                      Ordenar
                    </label>
                    <select
                      value={sort}
                      onChange={(e) => setSort(e.target.value)}
                      className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm hover:shadow-md"
                    >
                      <option value="default">Por defecto</option>
                      <option value="price-asc">Precio: menor a mayor</option>
                      <option value="price-desc">Precio: mayor a menor</option>
                      <option value="name">Nombre A-Z</option>
                    </select>
                  </div>

                  {/* Divider */}
                  <div className="border-t border-stone-200 dark:border-gray-700"></div>

                  {/* Price */}
                  <div className="space-y-3">
                    <label className="flex items-center justify-between text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                      <div className="flex items-center gap-2">
                        <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                          <DollarSign size={14} className="text-amber-600 dark:text-amber-400" />
                        </div>
                        Precio
                      </div>
                      <span className="text-amber-600 dark:text-amber-400 font-bold">${priceMax}</span>
                    </label>
                    <div className="relative pt-2">
                      <input
                        type="range"
                        min="0"
                        max="500"
                        step="5"
                        value={priceMax}
                        onChange={(e) => setPriceMax(Number(e.target.value))}
                        className="w-full h-2 bg-stone-200 dark:bg-gray-700 rounded-lg appearance-none cursor-pointer accent-amber-500"
                        style={{
                          background: `linear-gradient(to right, #f59e0b 0%, #f59e0b ${(priceMax / 500) * 100}%, #e7e5e4 ${(priceMax / 500) * 100}%, #e7e5e4 100%)`
                        }}
                      />
                      <div className="flex justify-between text-xs text-stone-500 dark:text-gray-400 mt-2">
                        <span>$0</span>
                        <span>$500</span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Footer con botón limpiar */}
                <div className="px-6 py-4 bg-stone-50 dark:bg-gray-900/50 border-t border-stone-200 dark:border-gray-700">
                  <button
                    onClick={() => {
                      setCategory('all');
                      setCollection('all');
                      setGender('all');
                      setSort('default');
                      setPriceMax(200);
                    }}
                    className="w-full px-4 py-3 rounded-xl bg-white dark:bg-gray-700 border-2 border-stone-300 dark:border-gray-600 text-sm font-bold text-stone-700 dark:text-gray-200 hover:bg-amber-50 dark:hover:bg-gray-600 hover:border-amber-400 dark:hover:border-amber-500 transition-all duration-200 shadow-sm hover:shadow-md flex items-center justify-center gap-2"
                  >
                    <X size={16} />
                    Limpiar filtros
                  </button>
                </div>
              </>
            )}
          </aside>

          {/* Grid de productos */}
          <div className="flex-1">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentPage + '-' + theme}
                initial={{ opacity: 0, y: 12 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -12 }}
                transition={{ duration: 0.45 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8"
              >
                {currentProducts.length > 0 ? (
                  currentProducts.map((product, index) => (
                    <motion.div
                      key={product.id}
                      initial={{ opacity: 0, scale: 0.98 }}
                      animate={{ opacity: 1, scale: 1 }}
                      exit={{ opacity: 0, scale: 0.98 }}
                      transition={{ duration: 0.35, delay: index * 0.03 }}
                    >
                      <ProductCard product={product} />
                    </motion.div>
                  ))
                ) : (
                  <p className="text-center text-stone-600 dark:text-gray-300 text-lg col-span-full">
                    No se encontraron productos que coincidan con los filtros.
                  </p>
                )}
              </motion.div>
            </AnimatePresence>

            {/* Paginación */}
            {totalPages > 1 && (
              <div className="flex justify-center items-center gap-2 mb-8 flex-wrap">
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition"
                  disabled={currentPage === 1}
                  aria-label="Anterior"
                >
                  <ArrowLeft size={18} />
                </button>

                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <button
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`px-4 py-2 rounded-full border transition-all duration-200 ${
                      currentPage === page
                        ? 'bg-amber-400 text-gray-900 border-amber-400 dark:bg-amber-600 dark:text-gray-900 dark:border-amber-600'
                        : 'bg-white text-stone-800 border-stone-300 hover:bg-amber-200 dark:bg-gray-700 dark:text-gray-300 dark:border-gray-600 dark:hover:bg-amber-500'
                    }`}
                    aria-current={currentPage === page ? 'page' : undefined}
                  >
                    {page}
                  </button>
                ))}

                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  className="p-2 rounded-full hover:bg-amber-200 dark:hover:bg-gray-700 transition"
                  disabled={currentPage === totalPages}
                  aria-label="Siguiente"
                >
                  <ArrowRight size={18} />
                </button>
              </div>
            )}

            {/* CTA final */}
            <div className="text-center">
              <button
                onClick={() => {
                  setCategory('all');
                  setCollection('all');
                  setGender('all');
                  setSort('default');
                  setPriceMax(500);
                  setCurrentPage(1);
                }}
                className="group inline-flex items-center gap-3 bg-gradient-to-r from-amber-400 to-amber-300 dark:from-amber-600 dark:to-amber-500 text-gray-900 dark:text-gray-900 px-10 py-4 rounded-full font-bold text-lg hover:shadow-lg transform hover:scale-105 transition-all duration-300"
              >
                Ver Todos los Productos
                <ArrowRight className="group-hover:translate-x-2 transition-transform duration-300" size={20} />
              </button>
            </div>
          </div>
        </div>

        {/* Drawer Filtros móviles */}
        <div
          className={`fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex justify-end transition-opacity duration-300 ${
            isFilterOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
          role="dialog"
          aria-modal={isFilterOpen}
          onClick={() => setIsFilterOpen(false)}
        >
          <aside
            onClick={(e) => e.stopPropagation()}
            className={`w-80 h-full shadow-2xl transform transition-transform duration-300 ease-in-out bg-white dark:bg-gray-800 flex flex-col
              ${isFilterOpen ? 'translate-x-0' : 'translate-x-full'}`}
          >
            {/* Header con gradiente */}
            <div className="bg-gradient-to-r from-amber-400 to-amber-500 dark:from-amber-600 dark:to-amber-700 px-6 py-5 relative">
              <button
                onClick={() => setIsFilterOpen(false)}
                className="absolute top-4 right-4 p-2 bg-white/20 rounded-full text-white hover:bg-white/30 transition-all duration-200"
                aria-label="Cerrar filtros"
              >
                <X size={20} />
              </button>
              
              <div className="flex items-center gap-3 pr-12">
                <div className="p-2 bg-white/20 rounded-lg backdrop-blur-sm">
                  <Filter size={20} className="text-white" />
                </div>
                <div>
                  <h3 className="text-lg font-bold text-white">Filtros</h3>
                  <p className="text-xs text-white/80">{filteredProducts.length} productos</p>
                </div>
              </div>
            </div>

            {/* Contenido scrolleable */}
            <div className="flex-1 overflow-y-auto px-6 py-5 space-y-5">
              {/* Collection */}
              <div className="space-y-3">
                <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                  <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                    <Layers size={14} className="text-amber-600 dark:text-amber-400" />
                  </div>
                  Colección
                </label>
                <select
                  value={collection}
                  onChange={(e) => { setCollection(e.target.value); setCurrentPage(1); }}
                  className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm"
                >
                  {collections.map((col) => (
                    <option key={col} value={col}>
                      {col === 'all' ? 'Todas las colecciones' : col.replace('-', ' ').toUpperCase()}
                    </option>
                  ))}
                </select>
              </div>

              {/* Divider */}
              <div className="border-t border-stone-200 dark:border-gray-700"></div>

              {/* Category */}
              <div className="space-y-3">
                <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                  <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                    <Tag size={14} className="text-amber-600 dark:text-amber-400" />
                  </div>
                  Categoría
                </label>
                <select
                  value={category}
                  onChange={(e) => { setCategory(e.target.value); setCurrentPage(1); }}
                  className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm"
                >
                  {categories.map((cat) => (
                    <option key={cat} value={cat}>
                      {cat === 'all' ? 'Todas las categorías' : cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </option>
                  ))}
                </select>
              </div>

              {/* Divider */}
              <div className="border-t border-stone-200 dark:border-gray-700"></div>

              {/* Gender */}
              <div className="space-y-3">
                <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                  <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                    <Zap size={14} className="text-amber-600 dark:text-amber-400" />
                  </div>
                  Género
                </label>
                <div className="grid grid-cols-3 gap-2">
                  <button
                    onClick={() => { setGender('all'); setCurrentPage(1); }}
                    className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                      gender === 'all'
                        ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                        : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                    }`}
                  >
                    Todos
                  </button>
                  <button
                    onClick={() => { setGender('men'); setCurrentPage(1); }}
                    className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                      gender === 'men'
                        ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                        : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                    }`}
                  >
                    Hombre
                  </button>
                  <button
                    onClick={() => { setGender('women'); setCurrentPage(1); }}
                    className={`px-3 py-2.5 rounded-lg text-xs font-bold transition-all duration-200 ${
                      gender === 'women'
                        ? 'bg-amber-400 text-gray-900 shadow-md scale-105'
                        : 'bg-stone-100 dark:bg-gray-700 text-stone-600 dark:text-gray-300 hover:bg-stone-200 dark:hover:bg-gray-600'
                    }`}
                  >
                    Mujer
                  </button>
                </div>
              </div>

              {/* Divider */}
              <div className="border-t border-stone-200 dark:border-gray-700"></div>

              {/* Sort */}
              <div className="space-y-3">
                <label className="flex items-center gap-2 text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                  <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                    <TrendingUp size={14} className="text-amber-600 dark:text-amber-400" />
                  </div>
                  Ordenar
                </label>
                <select
                  value={sort}
                  onChange={(e) => setSort(e.target.value)}
                  className="w-full px-4 py-3 bg-white dark:bg-gray-700 border-2 border-stone-200 dark:border-gray-600 rounded-xl focus:outline-none focus:border-amber-400 dark:focus:border-amber-500 transition-all duration-200 text-sm font-medium text-stone-700 dark:text-gray-200 shadow-sm"
                >
                  <option value="default">Por defecto</option>
                  <option value="price-asc">Precio: menor a mayor</option>
                  <option value="price-desc">Precio: mayor a menor</option>
                  <option value="name">Nombre A-Z</option>
                </select>
              </div>

              {/* Divider */}
              <div className="border-t border-stone-200 dark:border-gray-700"></div>

              {/* Price */}
              <div className="space-y-3">
                <label className="flex items-center justify-between text-sm font-bold text-stone-800 dark:text-gray-200 uppercase tracking-wide">
                  <div className="flex items-center gap-2">
                    <div className="p-1.5 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                      <DollarSign size={14} className="text-amber-600 dark:text-amber-400" />
                    </div>
                    Precio
                  </div>
                  <span className="text-amber-600 dark:text-amber-400 font-bold">${priceMax}</span>
                </label>
                <div className="relative pt-2">
                  <input
                    type="range"
                    min="0"
                    max="500"
                    step="5"
                    value={priceMax}
                    onChange={(e) => setPriceMax(Number(e.target.value))}
                    className="w-full h-2 bg-stone-200 dark:bg-gray-700 rounded-lg appearance-none cursor-pointer accent-amber-500"
                    style={{
                      background: `linear-gradient(to right, #f59e0b 0%, #f59e0b ${(priceMax / 500) * 100}%, #e7e5e4 ${(priceMax / 500) * 100}%, #e7e5e4 100%)`
                    }}
                  />
                  <div className="flex justify-between text-xs text-stone-500 dark:text-gray-400 mt-2">
                    <span>$0</span>
                    <span>$500</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Footer con botón limpiar */}
            <div className="px-6 py-4 bg-stone-50 dark:bg-gray-900/50 border-t border-stone-200 dark:border-gray-700">
              <button
                onClick={() => {
                  setCategory('all');
                  setCollection('all');
                  setGender('all');
                  setSort('default');
                  setPriceMax(200);
                }}
                className="w-full px-4 py-3 rounded-xl bg-white dark:bg-gray-700 border-2 border-stone-300 dark:border-gray-600 text-sm font-bold text-stone-700 dark:text-gray-200 hover:bg-amber-50 dark:hover:bg-gray-600 hover:border-amber-400 dark:hover:border-amber-500 transition-all duration-200 shadow-sm hover:shadow-md flex items-center justify-center gap-2"
              >
                <X size={16} />
                Limpiar filtros
              </button>
            </div>
          </aside>
        </div>
      </div>
    </section>
  );
};

export default ProductList;
