import React from 'react';
import { Star } from 'lucide-react';
import { useIntersectionObserver } from '../hooks/useIntersectionObserver';

const ProductCard = ({ product, index }) => {
  const [ref, isVisible] = useIntersectionObserver();

  return (
    <div 
      ref={ref}
      className={`bg-white rounded-3xl overflow-hidden shadow-lg transform transition-all duration-500 group ${
        isVisible ? 'animate-scale-in' : 'opacity-0'
      }`}
      style={{ animationDelay: `${index * 0.1}s` }}
    >
      <div className="relative overflow-hidden">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-80 object-cover group-hover:scale-105 transition-transform duration-700 rounded-t-3xl"
        />
        {product.discount && (
          <div className="absolute top-4 left-4 bg-gradient-to-r from-pink-200 to-red-200 text-purple-700 px-4 py-2 rounded-full text-sm font-bold shadow animate-pulse-slow">
            -{product.discount}%
          </div>
        )}
      </div>
      
      <div className="p-6">
        <div className="flex items-center gap-1 mb-3">
          {[...Array(5)].map((_, i) => (
            <Star key={i} size={16} className="fill-yellow-300 text-yellow-300" />
          ))}
          <span className="text-sm text-gray-500 ml-2 font-semibold">(128)</span>
        </div>
        
        <h3 className="text-xl font-bold text-purple-700 mb-2">
          {product.name}
        </h3>
        <p className="text-sm text-gray-500 mb-4 font-medium">{product.category}</p>
        
        <div className="flex items-center gap-2">
          {product.originalPrice && (
            <span className="text-gray-400 line-through text-sm">${product.originalPrice}</span>
          )}
          <span className="text-3xl font-bold text-purple-800">
            ${product.price}
          </span>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
