import React from 'react';

const Novedades = () => {
  const novedades = [
    {
      id: 1,
      title: 'Campera urbana 2025',
      category: 'Hombre',
      description: 'Ligera y cómoda, ideal para la ciudad.',
      image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 2,
      title: 'Vestido verano 2025',
      category: 'Mujer',
      description: 'Fresco y elegante para días soleados.',
      image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 3,
      title: 'Remera clásica unisex',
      category: 'Unisex',
      description: 'Básica y versátil para cualquier ocasión.',
      image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=800&q=80',
    },
    {
      id: 4,
      title: 'Pantalón cargo',
      category: 'Hombre',
      description: 'Cómodo y funcional, con bolsillos amplios.',
      image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=800&q=80',
    },
  ];

  return (
    <section className="py-16 bg-yellow-100">
      <div className="container mx-auto px-4">
        <h2 className="text-4xl font-extrabold mb-12 text-center text-gray-900">
          Novedades
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
          {novedades.map((item) => (
            <div
              key={item.id}
              className="relative group bg-white rounded-2xl shadow-md overflow-hidden transition-all duration-300 hover:shadow-xl hover:-translate-y-2"
            >
              <img
                src={item.image}
                alt={item.title}
                className="w-full h-56 object-cover transition-transform duration-500 group-hover:scale-105"
              />
              <div className="p-6">
                <h3 className="text-2xl font-bold mb-1 text-gray-900">{item.title}</h3>
                <p className="text-sm text-gray-500 mb-3 uppercase tracking-wide">{item.category}</p>
                <p className="text-gray-700 mb-4">{item.description}</p>
                <button className="px-4 py-2 bg-yellow-400 text-white rounded-full font-semibold shadow hover:bg-yellow-500 transition-colors">
                  Ver más
                </button>
              </div>
              <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-10 transition-opacity rounded-2xl"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Novedades;
