import React, { useState, useEffect, useCallback } from 'react';
import { Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') scrollToProducts();
        else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
  } else {
        setCurrentPage(item.page);
        if (item.page === 'home') window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  // AnimaciÃ³n escalonada
  const menuItemVariants = {
    closed: { opacity: 0, x: -30 },
    open: (i) => ({
      opacity: 1,
      x: 0,
      transition: { delay: i * 0.1, duration: 0.4, ease: 'easeOut' },
    }),
  };

  return (
    <>
      {/* SVG oculto para el toggle */}
      <svg xmlns="http://www.w3.org/2000/svg" style={{ display: 'none' }}>
        <symbol xmlns="http://www.w3.org/2000/svg" viewBox="0 0 44 44" id="path">
          <path d="M22,22 L2,22 C2,11 11,2 22,2 C33,2 42,11 42,22"></path>
        </symbol>
      </svg>

      <header className="fixed w-full z-50 backdrop-blur-xl bg-white/80 dark:bg-gray-900/80 shadow-lg transition-all duration-500">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
          {/* Logo */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="text-2xl font-extrabold bg-gradient-to-r from-amber-500 to-orange-600 bg-clip-text text-transparent cursor-pointer"
            onClick={() => {
              setCurrentPage('home');
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
          >
            Tienda
          </motion.div>

          {/* NavegaciÃ³n desktop */}
          <nav className="hidden md:flex items-center gap-10">
            {menuItems.map((item) => (
              <motion.button
                key={item.name}
                onClick={() => handleMenuClick(item)}
                whileHover={{ y: -2 }}
                whileTap={{ scale: 0.95 }}
                className={`relative font-medium text-gray-700 dark:text-gray-300 transition-all duration-300 ${
                  currentPage === item.page ? 'text-amber-500' : 'hover:text-amber-500'
                }`}
              >
                {item.name}
                {currentPage === item.page && (
                  <motion.div
                    layoutId="activeIndicator"
                    className="absolute -bottom-1 left-0 right-0 h-0.5 bg-amber-500 rounded-full"
                    initial={false}
                    transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                  />
                )}
              </motion.button>
            ))}
          </nav>

          {/* Botones: tema y toggle menÃº */}
          <div className="flex items-center gap-3">
            {/* Toggle tema */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={toggleTheme}
              className="p-2.5 rounded-full bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 
                         hover:bg-amber-100 dark:hover:bg-amber-900 hover:text-amber-600 
                         transition-all duration-300 shadow-sm"
            >
              <motion.div animate={{ rotate: theme === 'dark' ? 360 : 0 }} transition={{ duration: 0.5 }}>
                {theme === 'dark' ? <Sun size={18} /> : <Moon size={18} />}
              </motion.div>
            </motion.button>

            {/* Toggle menÃº animado (Dribbble style) */}
            <label className="md:hidden toggle cursor-pointer select-none">
              <input
                type="checkbox"
                checked={isMenuOpen}
                onChange={(e) => setIsMenuOpen(e.target.checked)}
              />
              <div>
                <div>
                  <span></span>
                  <span></span>
                </div>
                <svg>
                  <use xlinkHref="#path" />
                </svg>
                <svg>
                  <use xlinkHref="#path" />
                </svg>
              </div>
            </label>
          </div>
        </div>

        {/* MenÃº mÃ³vil */}
        <AnimatePresence>
          {isMenuOpen && (
            <>
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 bg-black/40 backdrop-blur-sm z-40"
                onClick={() => setIsMenuOpen(false)}
              />

              <motion.nav
                initial={{ x: '100%' }}
                animate={{ x: 0 }}
                exit={{ x: '100%' }}
                transition={{ type: 'spring', stiffness: 320, damping: 32 }}
                className="fixed top-0 right-0 h-full w-80 max-w-full 
                           bg-white dark:bg-gray-900 
                           backdrop-blur-md z-50 shadow-2xl 
                           border-l border-gray-200 dark:border-gray-800
                           p-8 flex flex-col"
              >
                {/* BotÃ³n cerrar (X) */}
                <motion.button
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ delay: 0.1 }}
                  whileHover={{ scale: 1.1, rotate: 90 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={() => setIsMenuOpen(false)}
                  className="absolute top-6 right-6 p-2.5 rounded-full bg-gray-100 dark:bg-gray-800 
                             hover:bg-amber-100 dark:hover:bg-amber-900 transition-all duration-300 shadow-sm"
                  aria-label="Cerrar menÃº"
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" className="stroke-current text-gray-600 dark:text-gray-400">
                    <path d="M6 18L18 6" strokeWidth="2" strokeLinecap="round" />
                    <path d="M6 6L18 18" strokeWidth="2" strokeLinecap="round" />
                  </svg>
                </motion.button>

                {/* Ãtems centrados con espacio superior */}
                <div className="flex-1 flex flex-col justify-center mt-16">
                  <div className="flex flex-col gap-4">
                    {menuItems.map((item, i) => (
                      <motion.button
                        key={item.name}
                        custom={i}
                        variants={menuItemVariants}
                        initial="closed"
                        animate="open"
                        whileHover={{ x: 12, backgroundColor: 'rgba(251, 191, 36, 0.15)' }}
                        whileTap={{ scale: 0.98 }}
                        onClick={() => {
                          handleMenuClick(item);
                          setIsMenuOpen(false);
                        }}
                        className={`text-left text-xl font-medium py-4 px-6 rounded-2xl 
                                   transition-all duration-300 shadow-sm
                                   ${currentPage === item.page
                                     ? 'bg-gradient-to-r from-amber-500 to-orange-500 text-white shadow-amber-500/30'
                                     : 'bg-gray-50 dark:bg-gray-800 text-gray-800 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700'
                                   }`}
                      >
                        {item.name}
                      </motion.button>
                    ))}
                  </div>
                </div>
              </motion.nav>
            </>
          )}
        </AnimatePresence>
      </header>

      {/* Estilos del toggle (Tailwind + CSS puro) */}
      <style jsx>{`
        :root {
          --color: ${theme === 'dark' ? '#e5e7eb' : '#1f2937'};
          --active: #f59e0b;
        }

        [data-theme='dark'] {
          --color: #e5e7eb;
          --active: #f59e0b;
        }

        .toggle {
          display: block;
          cursor: pointer;
        }

        .toggle input {
          display: none;
        }

        .toggle input + div {
          width: 20px;
          height: 14px;
          position: relative;
        }

        .toggle input + div > div {
          position: absolute;
          left: 0;
          top: 0;
          right: 0;
          bottom: 0;
          transition: transform 0.5s ease;
        }

        .toggle input + div > div span {
          display: block;
          position: absolute;
          left: 0;
          right: 0;
        }

        .toggle input + div > div span:first-child {
          top: 0;
        }

        .toggle input + div > div span:last-child {
          bottom: 0;
        }

        .toggle input + div > div span:before,
        .toggle input + div > div span:after {
          content: '';
          display: block;
          width: 47%;
          height: 2px;
          border-radius: 1px;
          background: var(--color);
          position: absolute;
          backface-visibility: hidden;
          transition: transform 0.5s ease, border-radius 0.3s ease, background 0.4s ease;
        }

        .toggle input + div > div span:before {
          left: 0;
          transform-origin: 0 50%;
          transform: translate(1px, 0) scaleX(1.1);
        }

        .toggle input + div > div span:after {
          right: 0;
          transform-origin: 100% 50%;
          transform: translate(-1px, 0) scaleX(1.1);
        }

        .toggle input + div svg {
          display: block;
          fill: none;
          stroke: var(--color);
          stroke-width: 2px;
          width: 44px;
          height: 44px;
          stroke-linecap: round;
          position: absolute;
          left: 50%;
          top: 50%;
          margin: -22px 0 0 -22px;
          stroke-dasharray: 0 82.801 8 82.801;
          stroke-dashoffset: 82.801;
          transform-origin: 50% 50%;
          backface-visibility: hidden;
          transform: scale(1);
          transition: stroke-dashoffset 0.5s ease, stroke-dasharray 0.6s ease, transform 0.5s ease, stroke 0.4s ease;
        }

        .toggle input + div svg:nth-child(3) {
          transform: rotate(180deg) scale(1);
        }

        .toggle input:checked + div > div {
          transform: rotate(90deg);
        }

        .toggle input:checked + div > div span:before,
        .toggle input:checked + div > div span:after {
          background: var(--active);
        }

        .toggle input:checked + div > div span:first-child:before {
          transform: rotate(45deg) translate(2.2px, -3px) scaleX(1.05);
        }

        .toggle input:checked + div > div span:first-child:after {
          transform: rotate(-45deg) translate(-2.2px, -3px) scaleX(1.05);
        }

        .toggle input:checked + div > div span:last-child:before {
          transform: rotate(-45deg) translate(2.2px, 3px) scaleX(1.05);
        }

        .toggle input:checked + div > div span:last-child:after {
          transform: rotate(45deg) translate(-2.2px, 3px) scaleX(1.05);
        }

        .toggle input:checked + div svg {
          stroke-dashoffset: 62;
          stroke-dasharray: 0 82.801 62 82.801;
          transform: rotate(90deg);
          stroke: var(--active);
        }

        .toggle input:checked + div svg:nth-child(3) {
          transform: rotate(270deg);
        }
      `}</style>
    </>
  );
};

export default Header;
