import React, { useState, useEffect } from 'react';
import { Menu, X, Moon, Sun } from 'lucide-react';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' },
  ];

  const handleNavClick = (page) => {
    if (page === 'products') scrollToProducts();
    else window.scrollTo({ top: 0, behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled
          ? theme === 'light'
            ? 'bg-white/90 backdrop-blur-md shadow-md py-2'
            : 'bg-neutral-900/80 backdrop-blur-md shadow-lg py-2'
          : theme === 'light'
          ? 'bg-white/60 backdrop-blur-sm py-4'
          : 'bg-neutral-800/60 backdrop-blur-sm py-4'
      }`}
    >
      <nav className="container mx-auto px-6 flex justify-between items-center">
        <div className="flex items-center gap-4">
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="md:hidden text-gray-800 dark:text-gray-200"
          >
            {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
          </button>
          <button
            onClick={() => handleNavClick('home')}
            className="font-extrabold text-2xl tracking-tight text-gray-900 dark:text-white"
          >
            <span className="text-amber-500">Mi</span>Tienda
          </button>
        </div>

        <div className="hidden md:flex items-center space-x-8">
          {navItems.map((item) => (
            <button
              key={item.name}
              onClick={() => handleNavClick(item.page)}
              className="text-gray-800 dark:text-gray-200 hover:text-amber-500 transition-colors duration-300"
            >
              {item.name}
            </button>
          ))}
          <button
            onClick={toggleTheme}
            className="p-2 rounded-full transition-all duration-300 hover:bg-amber-100 dark:hover:bg-neutral-700"
          >
            {theme === 'light' ? <Moon size={22} /> : <Sun size={22} />}
          </button>
        </div>
      </nav>
    </header>
  );
};

export default Header;
