import React, { useState, useEffect, useCallback } from 'react';
import { Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => {
    setIsMenuOpen(false);
  }, [currentPage]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') scrollToProducts();
        else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
      } else {
        setCurrentPage(item.page);
        if (item.page === 'home') window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  const menuItemVariants = {
    closed: { opacity: 0, x: -30 },
    open: (i) => ({
      opacity: 1,
      x: 0,
      transition: { delay: i * 0.1, duration: 0.4, ease: 'easeOut' },
    }),
  };

  return (
    <>
      {/* SVG oculto para el toggle */}
      <svg xmlns="http://www.w3.org/2000/svg" style={{ display: 'none' }}>
        <symbol xmlns="http://www.w3.org/2000/svg" viewBox="0 0 44 44" id="path">
          <path d="M22,22 L2,22 C2,11 11,2 22,2 C33,2 42,11 42,22"></path>
        </symbol>
      </svg>

      <header className="fixed w-full z-50 backdrop-blur-xl bg-white/80 dark:bg-gray-900/80 shadow-lg transition-all duration-500">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
          {/* Logo */}
          <motion.div
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="text-2xl font-extrabold bg-gradient-to-r from-slate-700 to-slate-900 dark:from-gray-100 dark:to-gray-300 bg-clip-text text-transparent cursor-pointer"
            onClick={() => {
              setCurrentPage('home');
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
          >
            Tienda
          </motion.div>

          {/* Navegación desktop */}
          <nav className="hidden md:flex items-center gap-10">
            {menuItems.map((item) => (
              <motion.button
                key={item.name}
                onClick={() => handleMenuClick(item)}
                whileHover={{ y: -2 }}
                whileTap={{ scale: 0.95 }}
                className={`relative font-medium text-gray-700 dark:text-gray-300 transition-all duration-300 ${
                  currentPage === item.page ? 'text-slate-900 dark:text-white' : 'hover:text-slate-900 dark:hover:text-white'
                }`}
              >
                {item.name}
                {currentPage === item.page && (
                  <motion.div
                    layoutId="activeIndicator"
                    className="absolute -bottom-1 left-0 right-0 h-0.5 bg-gradient-to-r from-slate-600 to-slate-800 dark:from-gray-300 dark:to-gray-100 rounded-full"
                    initial={false}
                    transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                  />
                )}
              </motion.button>
            ))}
          </nav>

          {/* Botones: tema y toggle menú */}
          <div className="flex items-center gap-3">
            {/* Toggle tema */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={toggleTheme}
              className="p-2.5 rounded-full bg-gray-100 dark:bg-gray-800 text-gray-700 dark:text-gray-300 
                         hover:bg-slate-100 dark:hover:bg-gray-700 hover:text-slate-900 dark:hover:text-white 
                         transition-all duration-300 shadow-sm"
            >
              <motion.div animate={{ rotate: theme === 'dark' ? 360 : 0 }} transition={{ duration: 0.5 }}>
                {theme === 'dark' ? <Sun size={18} /> : <Moon size={18} />}
              </motion.div>
            </motion.button>

            {/* Toggle menú animado (Dribbble style) */}
            <label className="md:hidden toggle cursor-pointer select-none">
              <input
                type="checkbox"
                checked={isMenuOpen}
                onChange={(e) => setIsMenuOpen(e.target.checked)}
              />
              <div>
                <div>
                  <span></span>
                  <span></span>
                </div>
                <svg>
                  <use xlinkHref="#path" />
                </svg>
                <svg>
                  <use xlinkHref="#path" />
                </svg>
              </div>
            </label>
          </div>
        </div>

        {/* Menú móvil: fondo sólido, diseño moderno */}
        <AnimatePresence>
          {isMenuOpen && (
            <>
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="fixed inset-0 bg-black/30 backdrop-blur-sm z-40"
                onClick={() => setIsMenuOpen(false)}
              />

              <motion.nav
                initial={{ x: '100%' }}
                animate={{ x: 0 }}
                exit={{ x: '100%' }}
                transition={{ type: 'spring', stiffness: 320, damping: 32 }}
                className="fixed top-0 right-0 h-full w-80 max-w-full 
                           bg-white/98 dark:bg-gray-900/98 
                           backdrop-blur-xl z-50 shadow-2xl 
                           border-l border-gray-200/50 dark:border-gray-800/50
                           p-8 flex flex-col"
              >
                {/* Botón cerrar */}
                <motion.button
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ delay: 0.1 }}
                  whileHover={{ scale: 1.1, rotate: 90 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={() => setIsMenuOpen(false)}
                  className="absolute top-6 right-6 p-2.5 rounded-full bg-gray-100 dark:bg-gray-800 
                             hover:bg-slate-100 dark:hover:bg-gray-700 transition-all duration-300 shadow-sm"
                  aria-label="Cerrar menú"
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" className="stroke-current text-gray-600 dark:text-gray-400">
                    <path d="M6 18L18 6" strokeWidth="2" strokeLinecap="round" />
                    <path d="M6 6L18 18" strokeWidth="2" strokeLinecap="round" />
                  </svg>
                </motion.button>

                {/* Ítems centrados: diseño moderno */}
                <div className="flex-1 flex flex-col justify-center mt-16">
                  <div className="flex flex-col gap-3">
                    {menuItems.map((item, i) => (
                      <motion.button
                        key={item.name}
                        custom={i}
                        variants={menuItemVariants}
                        initial="closed"
                        animate="open"
                        whileHover={{ 
                          x: 8, 
                          backgroundColor: theme === 'dark' ? 'rgba(255,255,255,0.05)' : 'rgba(0,0,0,0.03)',
                          boxShadow: '0 8px 25px rgba(0,0,0,0.08)'
                        }}
                        whileTap={{ scale: 0.98 }}
                        onClick={() => {
                          handleMenuClick(item);
                          setIsMenuOpen(false);
                        }}
                        className={`relative text-left text-xl font-medium py-4 px-6 rounded-2xl 
                                   transition-all duration-300 overflow-hidden
                                   ${currentPage === item.page
                                     ? 'text-slate-900 dark:text-white'
                                     : 'text-gray-600 dark:text-gray-400'
                                   }`}
                      >
                        <span className="relative z-10">{item.name}</span>
                        
                        {/* Subrayado activo animado */}
                        {currentPage === item.page && (
                          <motion.div
                            layoutId="mobileActive"
                            className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-slate-600 to-slate-800 dark:from-gray-300 dark:to-gray-100"
                            initial={false}
                            transition={{ type: 'spring', stiffness: 500, damping: 30 }}
                          />
                        )}
                      </motion.button>
                    ))}
                  </div>
                </div>
              </motion.nav>
            </>
          )}
        </AnimatePresence>
      </header>

      {/* Estilos del toggle (adaptado al tema) */}
      <style jsx>{`
        :root {
          --toggle-color: ${theme === 'dark' ? '#9ca3af' : '#4b5563'};
          --toggle-active: ${theme === 'dark' ? '#f3f4f6' : '#1f2937'};
        }

        .toggle input + div > div span:before,
        .toggle input + div > div span:after {
          background: var(--toggle-color) !important;
        }

        .toggle input + div svg {
          stroke: var(--toggle-color) !important;
        }

        .toggle input:checked + div > div span:before,
        .toggle input:checked + div > div span:after {
          background: var(--toggle-active) !important;
        }

        .toggle input:checked + div svg {
          stroke: var(--toggle-active) !important;
        }
      `}</style>
    </>
  );
};

export default Header;
