import React, { useState, useRef } from 'react';
import { Sparkles, ArrowRight, ArrowLeft, Filter, X } from 'lucide-react';
import ProductCard from './ProductCard';
import { products } from '../data/products';

const ProductList = () => {
  const [category, setCategory] = useState('all');
  const [gender, setGender] = useState('all');
  const [sort, setSort] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 200]);
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);

  const productsPerPage = 6;
  const categories = ['all', ...new Set(products.map(p => p.category)), 'Calzado'];

  const containerRef = useRef(null); // <-- ref para el scroll

  const filteredProducts = products
    .filter(p => (category === 'all' ? true : p.category === category))
    .filter(p => (gender === 'all' ? true : p.gender === gender))
    .filter(p => p.price >= priceRange[0] && p.price <= priceRange[1])
    .sort((a, b) => {
      if (sort === 'price-asc') return a.price - b.price;
      if (sort === 'price-desc') return b.price - a.price;
      if (sort === 'name') return a.name.localeCompare(b.name);
      return 0;
    });

  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, startIndex + productsPerPage);

  const handlePageChange = (page) => {
    if (page < 1 || page > totalPages) return;
    setCurrentPage(page);
    if (window.innerWidth < 768) setIsFilterOpen(false);

    // Hacer scroll al inicio de los productos
    if (containerRef.current) {
      containerRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="productos" className="relative bg-gradient-to-b from-amber-100 via-white to-white py-20">
      <div className="container mx-auto px-4" ref={containerRef}>
        {/* Título y filtros... el resto igual */}
        {/* Productos */}
        <div className="flex-1">
          {currentProducts.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 mb-8">
              {currentProducts.map((product, index) => (
                <ProductCard key={product.id} product={product} index={index} />
              ))}
            </div>
          ) : (
            <p className="text-center text-stone-600 text-lg mb-12">
              No se encontraron productos que coincidan con los filtros seleccionados.
            </p>
          )}

          {/* Paginación */}
          {totalPages > 1 && (
            <div className="flex justify-center items-center gap-2 mb-12 flex-wrap">
              <button
                onClick={() => handlePageChange(currentPage - 1)}
                className="p-2 rounded-full hover:bg-amber-200 transition"
                disabled={currentPage === 1}
              >
                <ArrowLeft size={20} />
              </button>

              {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                <button
                  key={page}
                  onClick={() => handlePageChange(page)}
                  className={`px-4 py-2 rounded-full border ${
                    currentPage === page
                      ? 'bg-amber-400 text-gray-900 border-amber-400'
                      : 'bg-white text-stone-800 border-stone-300 hover:bg-amber-200'
                  } transition-all`}
                >
                  {page}
                </button>
              ))}

              <button
                onClick={() => handlePageChange(currentPage + 1)}
                className="p-2 rounded-full hover:bg-amber-200 transition"
                disabled={currentPage === totalPages}
              >
                <ArrowRight size={20} />
              </button>
            </div>
          )}
        </div>
      </div>
    </section>
  );
};

export default ProductList;
