import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import ProductList from './components/ProductList';
import Contact from './components/Contact';
import Footer from './components/Footer';

const Home = ({ scrollToProducts }) => {
  return (
    <>
      <Hero scrollToProducts={scrollToProducts} />
      <ProductList />
      <Contact />
    </>
  );
};

const ContactPage = () => {
  return (
    <div className="pt-20">
      <Contact />
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [theme, setTheme] = useState('light'); // ðŸŒ™ modo oscuro / claro
  const [language, setLanguage] = useState('es'); // ðŸŒŽ idiomas (es/en)

  // Aplica el tema al <html>
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  const scrollToProducts = () => {
    const element = document.getElementById('productos');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setCurrentPage('home');
  };

  return (
    <div
      className={`min-h-screen flex flex-col transition-colors duration-500 ${
        theme === 'dark'
          ? 'bg-gray-950 text-gray-100'
          : 'bg-gray-50 text-gray-900'
      }`}
    >
      <Header
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
        scrollToProducts={scrollToProducts}
        theme={theme}
        setTheme={setTheme}
        language={language}
        setLanguage={setLanguage}
      />

      <main className="flex-grow">
        {currentPage === 'home' && <Home scrollToProducts={scrollToProducts} />}
        {currentPage === 'contact' && <ContactPage />}
      </main>

      <Footer />
    </div>
  );
};

export default App;
