import React, { useRef } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';

// --- Datos de productos ---
const novedadesData = [
  {
    id: 1,
    title: 'Campera Urbana 2025',
    category: 'Hombre',
    price: '$89.900',
    description: 'Ligera, impermeable y con diseño moderno. Ideal para la ciudad.',
    image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=1200&q=80',
    badgeColor: 'bg-blue-100 text-blue-800',
  },
  {
    id: 2,
    title: 'Vestido Verano 2025',
    category: 'Mujer',
    price: '$62.500',
    description: 'Fresco, fluido y elegante. Perfecto para días soleados.',
    image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=1200&q=80',
    badgeColor: 'bg-pink-100 text-pink-800',
  },
  {
    id: 3,
    title: 'Remera Clásica Unisex',
    category: 'Unisex',
    price: '$24.900',
    description: 'Algodón premium, corte perfecto. Tu esencial diario.',
    image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=1200&q=80',
    badgeColor: 'bg-gray-100 text-gray-800',
  },
  {
    id: 4,
    title: 'Pantalón Cargo',
    category: 'Hombre',
    price: '$74.900',
    description: 'Resistente, funcional y con bolsillos amplios.',
    image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=1200&q=80',
    badgeColor: 'bg-green-100 text-green-800',
  },
];

// --- Subcomponentes ---
const Badge = ({ category, color }) => (
  <span className={`absolute top-4 left-4 px-3 py-1 text-xs sm:text-sm font-semibold rounded-full ${color} backdrop-blur-sm shadow-sm`}>
    {category}
  </span>
);

const Slide = ({ item }) => (
  <SwiperSlide>
    <div className="flex flex-col md:grid md:grid-cols-[60%_40%] h-full">
      {/* Imagen */}
      <div className="relative group">
        <img
          src={item.image}
          alt={item.title}
          className="w-full h-64 sm:h-80 md:h-full object-cover transition-transform duration-700 group-hover:scale-105"
          loading="lazy"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
        <Badge category={item.category} color={item.badgeColor} />
      </div>

      {/* Texto */}
      <div className="flex flex-col justify-center px-4 sm:px-6 lg:px-8 py-6 sm:py-8 bg-white/90 backdrop-blur-md max-w-md mx-auto md:max-w-none md:mx-0 text-center">
        <h3 className="text-xl sm:text-2xl md:text-3xl font-bold text-gray-900 mb-2 sm:mb-3">
          {item.title}
        </h3>
        <p className="text-sm sm:text-base text-gray-700 leading-relaxed mb-4 sm:mb-5">
          {item.description}
        </p>
        <span className="text-xl sm:text-2xl font-bold text-yellow-600 mb-5 sm:mb-6">
          {item.price}
        </span>
        <div className="flex justify-center">
          <button className="px-4 py-2 sm:px-5 sm:py-2.5 bg-yellow-500 text-gray-900 font-semibold rounded-full shadow-md hover:bg-yellow-600 hover:shadow-lg hover:scale-105 transition-transform duration-300 text-sm sm:text-base">
            Ver producto
          </button>
        </div>
      </div>
    </div>
  </SwiperSlide>
);

// --- Componente principal ---
const Novedades = () => {
  const swiperRef = useRef(null);

  return (
    <section className="py-16 sm:py-20 transition-colors duration-500" style={{ backgroundColor: '#fef3c7' }}>
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        {/* Encabezado */}
        <div className="text-center mb-10 sm:mb-14">
          <h2 className="text-3xl sm:text-5xl font-extrabold text-gray-900 mb-3 sm:mb-4">Novedades</h2>
          <p className="text-base sm:text-lg text-gray-700 max-w-2xl mx-auto">
            Descubrí lo último en estilo, confort y tendencia para esta temporada.
          </p>
        </div>

        {/* Carrusel */}
        <div
          onMouseEnter={() => swiperRef.current?.autoplay?.stop()}
          onMouseLeave={() => swiperRef.current?.autoplay?.start()}
        >
          <Swiper
            modules={[Pagination, Autoplay]}
            spaceBetween={20}
            slidesPerView={1}
            pagination={{ clickable: true, el: '.novedades-pagination' }}
            autoplay={{ delay: 4000, disableOnInteraction: false }}
            loop
            onSwiper={(swiper) => (swiperRef.current = swiper)}
            className="relative rounded-2xl sm:rounded-3xl shadow-xl overflow-hidden"
          >
            {novedadesData.map((item) => (
              <Slide key={item.id} item={item} />
            ))}
          </Swiper>

          {/* Paginación */}
          <div className="novedades-pagination flex justify-center mt-6 sm:mt-8 gap-3"></div>
        </div>
      </div>

      {/* Estilo bullets */}
      <style jsx>{`
        .novedades-pagination .swiper-pagination-bullet {
          width: 10px;
          height: 10px;
          background-color: #fbbf24;
          opacity: 0.6;
          transition: all 0.3s ease;
        }
        .novedades-pagination .swiper-pagination-bullet-active {
          transform: scale(1.4);
          opacity: 1;
          background-color: #facc15;
        }
      `}</style>
    </section>
  );
};

export default Novedades;
