import React, { useState, useEffect, useContext } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useScrollAnimation } from '../hooks/useScrollAnimation';
import { ThemeContext } from '../context/ThemeContext';

const Header = ({ setCurrentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const scrollY = useScrollAnimation();
  const { theme, toggleTheme } = useContext(ThemeContext);

  useEffect(() => {
    setIsScrolled(scrollY > 50);
  }, [scrollY]);

  const navItems = [
    { name: 'Inicio', page: 'home' },
    { name: 'Productos', page: 'products' },
    { name: 'Contacto', page: 'contact' },
  ];

  const handleNavClick = (page) => {
    if (page === 'products') scrollToProducts();
    else window.scrollTo({ top: 0, behavior: 'smooth' });
    setIsMenuOpen(false);
  };

  return (
    <header
      className={`fixed w-full top-0 z-50 transition-all duration-500 ${
        isScrolled
          ? 'bg-white/90 dark:bg-neutral-900/90 backdrop-blur-md shadow-md py-2'
          : 'bg-white/60 dark:bg-neutral-900/60 backdrop-blur-sm py-4'
      }`}
    >
      <nav className="container mx-auto px-6">
        <div className="flex items-center justify-between relative">
          {/* Botón menú móvil */}
          <div className="flex items-center md:hidden">
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="transition-transform duration-300 hover:scale-110 text-gray-800 dark:text-gray-200 hover:text-amber-500"
            >
              {isMenuOpen ? <X size={28} /> : <Menu size={28} />}
            </button>
          </div>

          {/* Logo */}
          <button
            onClick={() => handleNavClick('home')}
            className="absolute left-1/2 transform -translate-x-1/2 md:static md:transform-none flex items-center space-x-2 font-extrabold text-2xl tracking-tight hover:scale-105 transition-transform duration-300 text-gray-900 dark:text-gray-100"
          >
            <span className="text-amber-500">Mi</span>
            <span>Tienda</span>
          </button>

          {/* Navegación Desktop */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="relative font-medium text-lg transition-all duration-300 group text-gray-800 dark:text-gray-200 hover:text-amber-500"
              >
                {item.name}
                <span className="absolute bottom-0 left-0 w-0 h-0.5 bg-amber-400 group-hover:w-full transition-all duration-300 rounded"></span>
              </button>
            ))}

            {/* Toggle modo oscuro con animación */}
            <button
              onClick={toggleTheme}
              className="p-2 rounded-full bg-neutral-200 dark:bg-neutral-700 hover:scale-110 transition-transform duration-300"
            >
              <AnimatePresence mode="wait" initial={false}>
                <motion.span
                  key={theme}
                  initial={{ rotate: -90, opacity: 0, scale: 0.5 }}
                  animate={{ rotate: 0, opacity: 1, scale: 1 }}
                  exit={{ rotate: 90, opacity: 0, scale: 0.5 }}
                  transition={{ duration: 0.4, ease: 'easeInOut' }}
                >
                  {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
                </motion.span>
              </AnimatePresence>
            </button>
          </div>
        </div>

        {/* Menú móvil */}
        <div
          className={`md:hidden fixed inset-0 z-40 bg-black/20 backdrop-blur-sm transition-opacity duration-300 ease-in-out ${
            isMenuOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
          }`}
        >
          <div
            className={`bg-gradient-to-br from-blue-100 via-amber-50 to-white dark:from-neutral-900 dark:via-neutral-800 dark:to-neutral-900 w-full h-screen p-8 flex flex-col justify-center items-center space-y-8 transform transition-transform duration-500 ease-in-out ${
              isMenuOpen ? 'translate-y-0' : '-translate-y-full'
            } overflow-y-auto relative`}
          >
            <button
              onClick={() => setIsMenuOpen(false)}
              className="absolute top-6 left-6 text-gray-900 dark:text-gray-100 hover:text-amber-500 transition-colors"
            >
              <X size={32} />
            </button>

            {navItems.map((item) => (
              <button
                key={item.name}
                onClick={() => handleNavClick(item.page)}
                className="text-3xl font-semibold transition-all duration-300 hover:scale-105 text-gray-900 dark:text-gray-100 hover:text-amber-500"
              >
                {item.name}
              </button>
            ))}

            {/* Toggle modo oscuro en móvil */}
            <button
              onClick={toggleTheme}
              className="mt-10 p-3 rounded-full bg-neutral-200 dark:bg-neutral-700 hover:scale-110 transition-transform duration-300"
            >
              <AnimatePresence mode="wait" initial={false}>
                <motion.span
                  key={theme}
                  initial={{ rotate: -90, opacity: 0, scale: 0.5 }}
                  animate={{ rotate: 0, opacity: 1, scale: 1 }}
                  exit={{ rotate: 90, opacity: 0, scale: 0.5 }}
                  transition={{ duration: 0.4, ease: 'easeInOut' }}
                >
                  {theme === 'dark' ? <Sun size={26} /> : <Moon size={26} />}
                </motion.span>
              </AnimatePresence>
            </button>
          </div>
        </div>
      </nav>
    </header>
  );
};

export default Header;
