import React from 'react';
import { motion } from 'framer-motion'; // npm install framer-motion
import { Link } from 'react-router-dom'; // Si usas React Router

const Novedades = () => {
  const novedades = [
    {
      id: 1,
      title: 'Campera Urbana 2025',
      category: 'Hombre',
      price: '$89.900',
      description: 'Ligera, impermeable y perfecta para la ciudad.',
      image: 'https://images.unsplash.com/photo-1618354699183-540c1e9e7b0f?auto=format&fit=crop&w=800&q=80',
      badgeColor: 'bg-blue-100 text-blue-800',
    },
    {
      id: 2,
      title: 'Vestido Verano 2025',
      category: 'Mujer',
      price: '$62.500',
      description: 'Fresco, fluido y elegante para días soleados.',
      image: 'https://images.unsplash.com/photo-1520975692437-53db7f5e8f3d?auto=format&fit=crop&w=800&q=80',
      badgeColor: 'bg-pink-100 text-pink-800',
    },
    {
      id: 3,
      title: 'Remera Clásica Unisex',
      category: 'Unisex',
      price: '$24.900',
      description: 'Algodón premium, corte perfecto, esencial diario.',
      image: 'https://images.unsplash.com/photo-1602810313598-0479f091a1b2?auto=format&fit=crop&w=800&q=80',
      badgeColor: 'bg-gray-100 text-gray-800',
    },
    {
      id: 4,
      title: 'Pantalón Cargo',
      category: 'Hombre',
      price: '$74.900',
      description: 'Resistente, con bolsillos funcionales y ajuste ideal.',
      image: 'https://images.unsplash.com/photo-1593032465170-8048c84c2b22?auto=format&fit=crop&w=800&q=80',
      badgeColor: 'bg-green-100 text-green-800',
    },
  ];

  return (
    <section className="py-16 bg-gradient-to-b from-yellow-50 to-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-4xl sm:text-5xl font-extrabold text-gray-900 mb-4">
            Novedades
          </h2>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            Descubre las últimas tendencias de la temporada
          </p>
        </motion.div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 lg:gap-8">
          {novedades.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className="group"
            >
              <Link
                to={`/producto/${item.id}`}
                className="block bg-white rounded-2xl shadow-md hover:shadow-2xl transition-all duration-300 overflow-hidden focus:outline-none focus:ring-4 focus:ring-yellow-300"
                aria-label={`Ver detalles de ${item.title}`}
              >
                <div className="relative overflow-hidden">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="w-full h-64 object-cover transition-transform duration-700 group-hover:scale-110"
                    loading="lazy"
                  />
                  <div className="absolute top-3 left-3">
                    <span
                      className={`inline-block px-3 py-1 text-xs font-semibold rounded-full ${item.badgeColor}`}
                    >
                      {item.category}
                    </span>
                  </div>
                  <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-20 transition-opacity duration-300" />
                </div>

                <div className="p-5 space-y-3">
                  <h3 className="text-xl font-bold text-gray-900 line-clamp-1 group-hover:text-yellow-600 transition-colors">
                    {item.title}
                  </h3>
                  <p className="text-sm text-gray-600 line-clamp-2">{item.description}</p>
                  <div className="flex items-center justify-between">
                    <span className="text-2xl font-bold text-gray-900">{item.price}</span>
                    <span className="text-sm text-yellow-600 font-medium">Ver detalle →</span>
                  </div>
                </div>
              </Link>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Novedades;
