import React, { useState, useEffect, useCallback } from 'react';
import { Menu, X, Sun, Moon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();

  useEffect(() => setIsMenuOpen(false), [currentPage]);

  const handleMenuClick = useCallback(
    (item) => {
      if (item.name === 'Productos') {
        if (currentPage === 'home') scrollToProducts();
        else {
          setCurrentPage('home');
          setTimeout(() => scrollToProducts(), 300);
        }
      } else {
        setCurrentPage(item.page);
      }
      setIsMenuOpen(false);
    },
    [currentPage, setCurrentPage, scrollToProducts]
  );

  const menuItems = [
    { name: 'Home', page: 'home' },
    { name: 'Productos', page: 'home' },
    { name: 'Contacto', page: 'contact' },
  ];

  const buttonVariants = {
    hover: { scale: 1.08, y: -2, boxShadow: '0px 6px 15px rgba(0,0,0,0.15)' },
    tap: { scale: 0.95, y: 0, boxShadow: '0px 2px 6px rgba(0,0,0,0.1)' },
  };

  const menuVariants = {
    hidden: { x: '100%', opacity: 0 },
    visible: {
      x: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 120,
        damping: 20,
        when: 'beforeChildren',
        staggerChildren: 0.08,
      },
    },
    exit: { x: '100%', opacity: 0, transition: { duration: 0.3 } },
  };

  const itemVariants = {
    hidden: { x: 50, opacity: 0 },
    visible: { x: 0, opacity: 1, transition: { duration: 0.4, ease: 'easeOut' } },
  };

  return (
    <header className="fixed w-full z-50 backdrop-blur-md bg-white/70 dark:bg-gray-900/80 shadow-md transition-colors duration-500 ease-in-out">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 flex items-center justify-between h-20">
        {/* Logo */}
        <motion.div
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          className="text-2xl font-extrabold text-gray-900 dark:text-gray-100 cursor-pointer hover:text-amber-500 transition-colors duration-500"
          onClick={() => setCurrentPage('home')}
        >
          Tienda
        </motion.div>

        {/* Desktop nav */}
        <nav className="hidden md:flex items-center gap-8">
          {menuItems.map((item) => (
            <motion.button
              key={item.name}
              onClick={() => handleMenuClick(item)}
              variants={buttonVariants}
              whileHover="hover"
              whileTap="tap"
              className={`relative font-semibold transition-colors duration-300 ease-in-out text-gray-900 dark:text-gray-200 focus:outline-none
                ${currentPage === item.page ? 'text-amber-400' : 'hover:text-amber-400'}
              `}
            >
              {item.name}
            </motion.button>
          ))}
        </nav>

        {/* Botones: modo oscuro y menú móvil */}
        <div className="flex items-center gap-4 relative z-50">
          {/* Toggle tema */}
          <motion.div
            whileHover={{ scale: 1.15, rotate: 10, boxShadow: '0px 6px 15px rgba(251,191,36,0.5)' }}
            whileTap={{ scale: 0.95, rotate: -10 }}
            className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
              transition-all duration-500 shadow-lg cursor-pointer select-none"
            onClick={(e) => { e.preventDefault(); e.stopPropagation(); toggleTheme(); }}
          >
            {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
          </motion.div>

          {/* Botón menú móvil */}
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            className="md:hidden p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 
            hover:bg-amber-400 dark:hover:bg-amber-500 shadow-lg transition-all duration-500"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            {isMenuOpen ? <X size={26} /> : <Menu size={26} />}
          </motion.button>
        </div>
      </div>

      {/* Mobile nav */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.nav
            variants={menuVariants}
            initial="hidden"
            animate="visible"
            exit="exit"
            className="md:hidden fixed top-0 right-0 w-3/4 h-full bg-white/90 dark:bg-gray-900/90 backdrop-blur-xl shadow-2xl z-40 overflow-hidden"
          >
            <div className="flex flex-col mt-24 p-6 gap-6">
              {menuItems.map((item) => (
                <motion.button
                  key={item.name}
                  variants={itemVariants}
                  onClick={() => handleMenuClick(item)}
                  className="text-gray-900 dark:text-gray-200 font-semibold py-3 px-4 rounded-xl hover:bg-amber-200 dark:hover:bg-amber-500 transition-colors duration-300 focus:outline-none shadow-sm"
                >
                  {item.name}
                </motion.button>
              ))}
            </div>
          </motion.nav>
        )}
      </AnimatePresence>
    </header>
  );
};

export default Header;
