import React, { useState, useEffect, useCallback } from "react";
import { Menu, X, Sun, Moon } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { useTheme } from "../context/ThemeContext";

const Header = ({ setCurrentPage, currentPage, scrollToProducts }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { theme, toggleTheme } = useTheme();
  const isDark = theme === "dark";

  const handleMenuToggle = useCallback(() => {
    setIsMenuOpen((prev) => !prev);
  }, []);

  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "auto";
    }
  }, [isMenuOpen]);

  const links = [
    { label: "Inicio", page: "home" },
    { label: "Productos", action: scrollToProducts },
    { label: "Novedades", page: "novedades" },
    { label: "Contacto", page: "contact" },
  ];

  return (
    <header
      className={`fixed top-0 left-0 w-full z-50 backdrop-blur-md transition-all duration-500 ${
        isDark ? "bg-black/40" : "bg-white/40"
      }`}
    >
      <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
        <h1
          className={`text-2xl font-bold tracking-wide cursor-pointer ${
            isDark ? "text-white" : "text-gray-900"
          }`}
          onClick={() => setCurrentPage("home")}
        >
          E-Shop
        </h1>

        {/* Botón menú hamburguesa */}
        <div className="flex items-center gap-3 md:hidden">
          <button
            onClick={toggleTheme}
            className={`p-2 rounded-full border transition-colors duration-300 ${
              isDark
                ? "text-yellow-400 border-gray-700 hover:bg-gray-800"
                : "text-gray-700 border-gray-300 hover:bg-gray-100"
            }`}
          >
            {isDark ? <Sun size={18} /> : <Moon size={18} />}
          </button>

          <motion.button
            onClick={handleMenuToggle}
            className="relative w-9 h-9 flex flex-col justify-center items-center group"
            whileTap={{ scale: 0.9 }}
          >
            <span
              className={`block w-6 h-0.5 rounded-sm transition-all duration-300 ${
                isMenuOpen
                  ? "rotate-45 translate-y-[6px] bg-red-400"
                  : isDark
                  ? "bg-white"
                  : "bg-gray-800"
              }`}
            ></span>
            <span
              className={`block w-6 h-0.5 rounded-sm my-[5px] transition-all duration-300 ${
                isMenuOpen ? "opacity-0" : isDark ? "bg-white" : "bg-gray-800"
              }`}
            ></span>
            <span
              className={`block w-6 h-0.5 rounded-sm transition-all duration-300 ${
                isMenuOpen
                  ? "-rotate-45 -translate-y-[6px] bg-red-400"
                  : isDark
                  ? "bg-white"
                  : "bg-gray-800"
              }`}
            ></span>
          </motion.button>
        </div>

        {/* Menú en desktop */}
        <nav className="hidden md:flex items-center gap-8">
          {links.map((link, index) => (
            <motion.button
              key={index}
              onClick={() => {
                if (link.action) link.action();
                if (link.page) setCurrentPage(link.page);
              }}
              whileHover={{ scale: 1.1 }}
              className={`text-base font-medium transition-colors duration-300 hover:text-red-400 ${
                currentPage === link.page
                  ? "text-red-400"
                  : isDark
                  ? "text-white"
                  : "text-gray-800"
              }`}
              style={{ textDecoration: "none" }}
            >
              {link.label}
            </motion.button>
          ))}

          <button
            onClick={toggleTheme}
            className={`p-2 rounded-full border transition-colors duration-300 ${
              isDark
                ? "text-yellow-400 border-gray-700 hover:bg-gray-800"
                : "text-gray-700 border-gray-300 hover:bg-gray-100"
            }`}
          >
            {isDark ? <Sun size={18} /> : <Moon size={18} />}
          </button>
        </nav>
      </div>

      {/* Menú móvil */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.div
            initial={{ opacity: 0, y: -30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -30 }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
            className={`fixed top-16 left-0 w-full h-[calc(100vh-4rem)] flex flex-col items-center justify-center gap-8 text-lg backdrop-blur-lg transition-colors duration-500 ${
              isDark
                ? "bg-gradient-to-b from-gray-900/80 to-black/90"
                : "bg-gradient-to-b from-white/70 to-gray-100/90"
            }`}
          >
            {links.map((link, index) => (
              <motion.button
                key={index}
                onClick={() => {
                  if (link.action) link.action();
                  if (link.page) setCurrentPage(link.page);
                  setIsMenuOpen(false);
                }}
                whileHover={{ scale: 1.1 }}
                className={`text-2xl font-medium transition-all duration-300 hover:text-red-400 ${
                  isDark ? "text-white" : "text-gray-800"
                }`}
                style={{ textDecoration: "none" }}
              >
                {link.label}
              </motion.button>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </header>
  );
};

export default Header;
